// src/components/Register.jsx
import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    rol: 'franquiciado',
  });
  const [error, setError] = useState(null);

  const handleChange = e => setFormData({...formData, [e.target.name]: e.target.value});
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/auth/register/', formData);
      alert('Usuario registrado. Ya puedes iniciar sesi칩n.');
    } catch (err) {
      setError('Error registrando usuario');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p>{error}</p>}
      <input name="username" placeholder="Usuario" onChange={handleChange} required />
      <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
      <input name="password" type="password" placeholder="Contrase침a" onChange={handleChange} required />
      <select name="rol" onChange={handleChange} value={formData.rol}>
        <option value="franquiciado">Franquiciado</option>
        <option value="responsable">Responsable</option>
        <option value="coordinacion">Coordinaci칩n</option>
        <option value="agente">Agente</option>
      </select>
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
