import { useState } from "react";
import axios from "axios";

export default function ClientForm({ onSuccess }) {
  const [formData, setFormData] = useState({
    nombre: "",
    apellido1: "",
    apellido2: "",
    trato: "Sr.",
    sexo: "M",
    tipo_documento: "DNI",
    num_identificacion: "",
    direccion: "",
    info_adicional: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8000/api/clientes/", formData)
      .then(() => {
        if (onSuccess) onSuccess();
        setFormData({
          nombre: "",
          apellido1: "",
          apellido2: "",
          trato: "Sr.",
          sexo: "M",
          tipo_documento: "DNI",
          num_identificacion: "",
          direccion: "",
          info_adicional: "",
        });
      })
      .catch((err) => console.error(err));
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 shadow rounded space-y-2">
      <h2 className="text-xl font-bold mb-4">Añadir Cliente</h2>

      <input name="nombre" value={formData.nombre} onChange={handleChange} placeholder="Nombre" className="w-full border p-2 rounded" />
      <input name="apellido1" value={formData.apellido1} onChange={handleChange} placeholder="Primer Apellido" className="w-full border p-2 rounded" />
      <input name="apellido2" value={formData.apellido2} onChange={handleChange} placeholder="Segundo Apellido (opcional)" className="w-full border p-2 rounded" />
      
      <select name="trato" value={formData.trato} onChange={handleChange} className="w-full border p-2 rounded">
        <option value="Sr.">Sr.</option>
        <option value="Sra.">Sra.</option>
        <option value="Mr.">Mr.</option>
        <option value="Mrs.">Mrs.</option>
      </select>

      <select name="sexo" value={formData.sexo} onChange={handleChange} className="w-full border p-2 rounded">
        <option value="M">Masculino</option>
        <option value="F">Femenino</option>
        <option value="O">Otro</option>
      </select>

      <select name="tipo_documento" value={formData.tipo_documento} onChange={handleChange} className="w-full border p-2 rounded">
        <option value="DNI">DNI</option>
        <option value="NIF">NIF</option>
        <option value="PASAPORTE">Pasaporte</option>
      </select>

      <input name="num_identificacion" value={formData.num_identificacion} onChange={handleChange} placeholder="Número de Documento" className="w-full border p-2 rounded" />
      <input name="direccion" value={formData.direccion} onChange={handleChange} placeholder="Dirección" className="w-full border p-2 rounded" />
      <textarea name="info_adicional" value={formData.info_adicional} onChange={handleChange} placeholder="Información adicional" className="w-full border p-2 rounded" />

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Guardar</button>
    </form>
  );
}
