// src/components/PropertyDetail.jsx
import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function PropertyDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [inmueble, setInmueble] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8000/api/inmuebles/${id}/`)
      .then(res => setInmueble(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!inmueble) return <div>Cargando inmueble...</div>;

  return (
    <div className="bg-white p-4 shadow rounded max-w-3xl mx-auto">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 text-blue-600 hover:underline"
      >
        ← Volver
      </button>
      <h2 className="text-2xl font-bold mb-2">Inmueble en {inmueble.edificio?.nombre || "Edificio"}</h2>
      <p><strong>Propietario:</strong> {inmueble.propietario ? inmueble.propietario.nombre : "Sin propietario"}</p>
      <p><strong>Planta:</strong> {inmueble.planta}</p>
      <p><strong>Puerta:</strong> {inmueble.puerta}</p>
      <p><strong>Interior:</strong> {inmueble.interior || "-"}</p>
      <p><strong>Referencia Catastral:</strong> {inmueble.ref_catastral}</p>
      <p><strong>Estado Certificado:</strong> {inmueble.estado_certificado}</p>
      <p><strong>Emisiones CO2:</strong> {inmueble.emisiones_CO2 || "-"}</p>
      <p><strong>Consumo Energía:</strong> {inmueble.consumo_energia || "-"}</p>
      <p><strong>Habitaciones:</strong> {inmueble.habitaciones}</p>
      <p><strong>Baños:</strong> {inmueble.banos}</p>
      <p><strong>Estancias:</strong> {inmueble.estancias}</p>
      <p><strong>Balcón:</strong> {inmueble.balcon ? "Sí" : "No"}</p>
      <p><strong>Jardín:</strong> {inmueble.jardin ? "Sí" : "No"}</p>
      {/* Aquí puedes añadir más detalles o enlaces a copropietarios, inquilinos, etc. */}
    </div>
  );
}
