import { useState } from "react";
import { CalendarIcon } from "lucide-react";
import WeekView from "./WeekView";
import DayView from "./DayView";
import MonthView from "./MonthView";

const VISTAS = {
  DIA: "DÍA",
  SEMANA: "SEMANA",
  MES: "MES",
};

export default function Calendar() {
  const [vista, setVista] = useState(VISTAS.SEMANA);
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState(null);

  return (
    <div className="flex flex-col h-screen">
      <div className="flex items-center justify-between p-4 border-b bg-gray-50">
        <div className="flex gap-2 items-center">
          <CalendarIcon className="w-5 h-5" />
          <select
            value={vista}
            onChange={(e) => setVista(e.target.value)}
            className="border rounded px-2 py-1"
          >
            <option value={VISTAS.DIA}>Vista diaria</option>
            <option value={VISTAS.SEMANA}>Vista semanal</option>
            <option value={VISTAS.MES}>Vista mensual</option>
          </select>
        </div>
        <select
          className="border rounded px-2 py-1"
          value={usuarioSeleccionado || ""}
          onChange={(e) => setUsuarioSeleccionado(e.target.value)}
        >
          <option value="">Todos los usuarios</option>
        </select>
      </div>

      <div className="flex-1 overflow-auto">
        {vista === VISTAS.DIA && <DayView usuarioSeleccionado={usuarioSeleccionado} />}
        {vista === VISTAS.SEMANA && <WeekView usuarioSeleccionado={usuarioSeleccionado} />}
        {vista === VISTAS.MES && <MonthView usuarioSeleccionado={usuarioSeleccionado} />}
      </div>
    </div>
  );
}
