// src/components/ClientDetail.js
import { useState, useEffect } from "react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";

export default function ClientDetail({ clientId }) {
  const [client, setClient] = useState(null);
  const [activities, setActivities] = useState([]);
  const [showNewActivity, setShowNewActivity] = useState(false);

  useEffect(() => {
    if (clientId) cargarDatos();
  }, [clientId]);

  async function cargarDatos() {
    try {
      const token = localStorage.getItem("access_token");
      if (!token) return;

      // Cargar datos del cliente
      const resClient = await api.get(`/clientes/${clientId}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setClient(resClient.data);

      // Cargar actividades asociadas al cliente
      const resActivities = await api.get(`/actividades/?cliente=${clientId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setActivities(resActivities.data);
    } catch (error) {
      console.error("Error cargando datos cliente:", error);
    }
  }

  if (!client) return <p>Cargando cliente...</p>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">
        {client.nombre} {client.apellido1} {client.apellido2}
      </h1>

      <button
        className="mb-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        onClick={() => setShowNewActivity(true)}
      >
        Nueva actividad
      </button>

      {showNewActivity && (
        <NewActivityForm
          clienteId={client.id}
          clienteNombre={client.nombre}
          clienteApellido1={client.apellido1}
          clienteApellido2={client.apellido2}
          onCancel={() => setShowNewActivity(false)}
          onCreated={() => {
            setShowNewActivity(false);
            cargarDatos(); // Recarga cliente y actividades tras crear nueva
          }}
        />
      )}

      <h2 className="text-xl font-semibold mb-2">Actividades</h2>
      {activities.length === 0 ? (
        <p>No hay actividades.</p>
      ) : (
        <ul className="space-y-2">
          {activities.map((act) => (
            <li key={act.id} className="border p-2 rounded">
              <strong>{act.tipo}</strong> -{" "}
              {new Date(act.fecha_inicio).toLocaleString()} - {act.descripcion_empleado}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
