import { useState, useEffect, useRef } from "react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";
import {
  startHour,
  endHour,
  intervalMinutes,
  getDateFromSelection,
  getStyleForActividad,
} from "./calendarUtils";

const totalRows = ((endHour - startHour) * 60) / intervalMinutes;

const tipoColores = {
  Adquisición: "bg-yellow-400 text-black",
  Visita: "bg-blue-500 text-white",
  Llamada: "bg-green-500 text-white",
  "Contacto directo": "bg-red-500 text-white",
  Genérica: "bg-gray-500 text-white",
  Zona: "bg-orange-500 text-white",
  "Reunión o curso": "bg-purple-600 text-white",
};

export default function DayView({ usuariosSeleccionados }) {
  const [formVisible, setFormVisible] = useState(false);
  const [formPosition, setFormPosition] = useState({ x: 0, y: 0 });
  const [selection, setSelection] = useState(null);
  const [selectedCells, setSelectedCells] = useState([]);
  const [actividades, setActividades] = useState([]);
  const [actividadEditando, setActividadEditando] = useState(null);
  const calendarRef = useRef();
  const [draggingId, setDraggingId] = useState(null);
  const [resizingId, setResizingId] = useState(null);
  const cellHeight = 20;

  useEffect(() => {
    const fetchActividades = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await api.get("/actividades/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setActividades(res.data);
      } catch (err) {
        console.error("Error obteniendo actividades:", err);
      }
    };

    fetchActividades();
  }, [formVisible]);

  const actividadesHoy = actividades.filter((a) => {
    const inicio = new Date(a.fecha_inicio);
    const hoy = new Date();
    return (
      (!usuariosSeleccionados.length ||
        usuariosSeleccionados.includes(a.usuario_responsable?.id)) &&
      inicio.toDateString() === hoy.toDateString()
    );
  });

  // --- Lógica de solapamientos ---
  const franjas = {};
  actividadesHoy.forEach((actividad) => {
    const start = new Date(actividad.fecha_inicio);
    const end = new Date(actividad.fecha_fin);
    const startRow = Math.floor(((start.getHours() - startHour) * 60 + start.getMinutes()) / intervalMinutes);
    const endRow = Math.ceil(((end.getHours() - startHour) * 60 + end.getMinutes()) / intervalMinutes);
    for (let r = startRow; r < endRow; r++) {
      if (!franjas[r]) franjas[r] = [];
      franjas[r].push(actividad);
    }
  });

  const contarSolapamientos = (actividad) => {
    const start = new Date(actividad.fecha_inicio);
    const end = new Date(actividad.fecha_fin);
    const startRow = Math.floor(((start.getHours() - startHour) * 60 + start.getMinutes()) / intervalMinutes);
    const endRow = Math.ceil(((end.getHours() - startHour) * 60 + end.getMinutes()) / intervalMinutes);
    let max = 1;
    for (let r = startRow; r < endRow; r++) {
      max = Math.max(max, franjas[r]?.length || 1);
    }
    return max;
  };

  const posicionEnSolapamiento = (actividad) => {
    const start = new Date(actividad.fecha_inicio);
    const row = Math.floor(((start.getHours() - startHour) * 60 + start.getMinutes()) / intervalMinutes);
    const fila = franjas[row] || [];
    return fila.findIndex((a) => a.id === actividad.id);
  };

  const startDrag = (id) => setDraggingId(id);
  const startResize = (id) => setResizingId(id);

  const stopInteraction = async () => {
    if (!draggingId && !resizingId) return;

    const actividad = actividades.find((a) => a.id === (draggingId || resizingId));
    if (!actividad) return;

    const topPx = parseFloat(document.getElementById(`actividad-${actividad.id}`)?.style.top || "0");
    const heightPx = parseFloat(document.getElementById(`actividad-${actividad.id}`)?.style.height || "0");

    const newStartRow = Math.round((topPx - 64) / cellHeight);
    const newEndRow = Math.max(newStartRow + 1, Math.round((topPx + heightPx - 64) / cellHeight));

    const fecha = new Date();
    fecha.setHours(0, 0, 0, 0);
    const inicio = new Date(fecha);
    inicio.setMinutes(startHour * 60 + newStartRow * intervalMinutes);
    const fin = new Date(fecha);
    fin.setMinutes(startHour * 60 + newEndRow * intervalMinutes);

    const payload = {
      ...actividad,
      fecha_inicio: inicio.toISOString(),
      fecha_fin: fin.toISOString(),
    };

    try {
      const token = localStorage.getItem("accessToken");
      await api.put(`/actividades/${actividad.id}/`, payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
    } catch (err) {
      console.error("Error actualizando:", err);
    }

    setDraggingId(null);
    setResizingId(null);
  };

  const datosTiempo = selection ? getDateFromSelection(selection) : null;

  return (
    <div
      className="flex-1 overflow-auto relative"
      ref={calendarRef}
      onMouseUp={stopInteraction}
      onMouseLeave={stopInteraction}
    >
      <table className="w-full border-collapse">
        <thead className="sticky top-0 bg-white z-10">
          <tr>
            <th className="w-20" />
            <th className="text-xs font-semibold text-center border-l">
              <div>Hoy</div>
              <div className="text-[10px] text-gray-400">
                {new Date().toLocaleDateString()}
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: totalRows }).map((_, i) => {
            const hour = Math.floor((i * intervalMinutes) / 60) + startHour;
            const minute = (i * intervalMinutes) % 60;
            const timeLabel = `${hour.toString().padStart(2, "0")}:${minute
              .toString()
              .padStart(2, "0")}`;

            return (
              <tr key={i} className="h-5 border-b">
                <td className="text-xs pr-2 text-right w-20 text-gray-500 align-top">
                  {timeLabel}
                </td>
                <td className="border-l" />
              </tr>
            );
          })}
        </tbody>
      </table>

      {actividadesHoy.map((actividad) => {
        const style = getStyleForActividad(actividad);
        const total = contarSolapamientos(actividad);
        const posicion = posicionEnSolapamiento(actividad);
        const anchoContenedor = calendarRef.current?.clientWidth || 800;
        const cellWidth = anchoContenedor - 80;
        const width = cellWidth / Math.min(total, 6);
        const left = 80 + posicion * width;
        const claseColor = tipoColores[actividad.tipo] || "bg-black text-white";

        return (
          <div
            key={actividad.id}
            id={`actividad-${actividad.id}`}
            onMouseDown={(e) => {
              if (e.target.dataset.resize) return;
              startDrag(actividad.id);
            }}
            onClick={() => {
              if (draggingId || resizingId) return;
              setActividadEditando(actividad);
              setFormPosition({ x: left, y: style.top });
              setFormVisible(true);
            }}
            className={`absolute text-xs p-1 rounded cursor-move ${claseColor}`}
            style={{
              top: style.top,
              left,
              height: style.height,
              width: width - 2,
            }}
          >
            {actividad.descripcion_publica}
            <div
              data-resize
              onMouseDown={() => startResize(actividad.id)}
              className="w-full h-2 bg-white opacity-50 cursor-ns-resize absolute bottom-0 left-0"
            />
          </div>
        );
      })}

      {formVisible && (
        <div
          className="absolute z-50"
          style={{ top: formPosition.y, left: formPosition.x }}
        >
          <NewActivityForm
            actividad={actividadEditando}
            fechaInicial={datosTiempo?.fecha}
            horaInicioInicial={datosTiempo?.horaInicio}
            horaFinInicial={datosTiempo?.horaFin}
            onCancel={() => {
              setFormVisible(false);
              setSelectedCells([]);
              setActividadEditando(null);
            }}
            onCreated={() => {
              setFormVisible(false);
              setSelectedCells([]);
              setActividadEditando(null);
            }}
          />
        </div>
      )}
    </div>
  );
}
