// Calendar.js actualizado con funcionalidad para editar actividades desde el calendario
import { useState, useRef, useEffect } from "react";
import { CalendarIcon } from "lucide-react";
import NewCalendarActivity from "./NewCalendarActivity";
import api from "../api";

const startHour = 6;
const endHour = 24;
const intervalMinutes = 15;
const days = ["Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado", "Domingo"];

export default function Calendar() {
  const [formVisible, setFormVisible] = useState(false);
  const [formPosition, setFormPosition] = useState({ x: 0, y: 0 });
  const [selection, setSelection] = useState(null);
  const [selectedCells, setSelectedCells] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState(null);
  const [actividades, setActividades] = useState([]);
  const [actividadEditando, setActividadEditando] = useState(null);
  const calendarRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [startCell, setStartCell] = useState(null);
  const [currentMonth, setCurrentMonth] = useState("");
  const [currentYear, setCurrentYear] = useState("");

  const totalRows = ((endHour - startHour) * 60) / intervalMinutes;

  useEffect(() => {
    const fetchUsuarios = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await fetch("/api/usuarios/", {
          headers: { "Authorization": `Bearer ${token}` },
        });
        if (res.ok) setUsuarios(await res.json());
      } catch (error) {
        console.error("Error al obtener usuarios:", error);
      }
    };

    const fetchActividades = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await api.get("/actividades/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setActividades(res.data);
      } catch (err) {
        console.error("Error obteniendo actividades:", err);
      }
    };

    fetchUsuarios();
    fetchActividades();

    const date = new Date();
    setCurrentMonth(date.toLocaleString("es-ES", { month: "long" }));
    setCurrentYear(date.getFullYear());
  }, []);

  const handleMouseDown = (rowIndex, colIndex) => {
    setIsDragging(true);
    setStartCell({ rowIndex, colIndex });
    setSelectedCells([{ rowIndex, colIndex }]);
  };

  const handleMouseEnter = (rowIndex, colIndex) => {
    if (isDragging && startCell && colIndex === startCell.colIndex) {
      const minRow = Math.min(startCell.rowIndex, rowIndex);
      const maxRow = Math.max(startCell.rowIndex, rowIndex);
      const newSelection = [];
      for (let i = minRow; i <= maxRow; i++) {
        newSelection.push({ rowIndex: i, colIndex });
      }
      setSelectedCells(newSelection);
    }
  };

  const handleMouseUp = (endRowIndex, endColIndex) => {
    if (isDragging && startCell && endColIndex === startCell.colIndex) {
      const minRow = Math.min(startCell.rowIndex, endRowIndex);
      const maxRow = Math.max(startCell.rowIndex, endRowIndex);
      const col = startCell.colIndex;

      const top = minRow * 20 + 64;
      const left = col * 160 + 160;

      setFormPosition({ x: left, y: top });
      setSelection({ day: days[col], start: minRow, end: maxRow });
      setFormVisible(true);
      setActividadEditando(null); // nueva actividad
    }
    setIsDragging(false);
    setStartCell(null);
  };

  const getDateFromSelection = (selection) => {
    const today = new Date();
    const todayDay = (today.getDay() + 6) % 7;
    const targetDayIndex = days.indexOf(selection.day);
    let diffDays = targetDayIndex - todayDay;
    if (diffDays < 0) diffDays += 7;
    const baseDate = new Date(today);
    baseDate.setDate(today.getDate() + diffDays);
    const minutesFromStart = selection.start * intervalMinutes;
    baseDate.setHours(startHour + Math.floor(minutesFromStart / 60));
    baseDate.setMinutes(minutesFromStart % 60);
    return baseDate;
  };

  const getStyleForActividad = (actividad) => {
    const start = new Date(actividad.fecha_inicio);
    const end = new Date(actividad.fecha_fin);
    const col = (start.getDay() + 6) % 7;
    const minutesSinceStart = (start.getHours() - startHour) * 60 + start.getMinutes();
    const top = (minutesSinceStart / intervalMinutes) * 20 + 64;
    const height = ((end - start) / 1000 / 60 / intervalMinutes) * 20;
    return {
      top,
      left: col * 160 + 160,
      height,
    };
  };

  return (
    <div className="flex h-[calc(100vh-4rem)] select-none">
      {/* Filtros */}
      <div className="w-1/8 p-4 border-r bg-gray-50">
        <h3 className="font-semibold mb-2">Filtros</h3>
        <select
          className="w-full border rounded p-1 mb-2"
          value={usuarioSeleccionado || ""}
          onChange={(e) => setUsuarioSeleccionado(e.target.value)}
        >
          <option value="">Todos los usuarios</option>
          {usuarios.map((u) => (
            <option key={u.id} value={u.id}>
              {u.first_name || u.last_name
                ? `${u.first_name || ""} ${u.last_name || ""}`.trim()
                : u.username}
            </option>
          ))}
        </select>
      </div>

      {/* Calendario */}
      <div className="flex-1 overflow-auto relative" ref={calendarRef}>
        <div className="sticky top-0 bg-white z-10 flex items-center justify-between border-b px-4 py-2">
          <h2 className="text-lg font-semibold capitalize">
            <CalendarIcon className="inline-block w-5 h-5 mr-2" />
            {currentMonth} {currentYear}
          </h2>
        </div>

        <table className="w-full border-collapse">
          <thead className="sticky top-12 bg-white z-10">
            <tr>
              <th className="w-20" />
              {days.map((day, idx) => (
                <th key={idx} className="text-xs font-semibold text-center border-l">
                  <div>{day}</div>
                  <div className="text-[10px] text-gray-400">
                    {new Date().getDate() + idx}
                  </div>
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Array.from({ length: totalRows }).map((_, i) => {
              const hour = Math.floor((i * intervalMinutes) / 60) + startHour;
              const minute = (i * intervalMinutes) % 60;
              const timeLabel = `${hour.toString().padStart(2, "0")}:${minute
                .toString()
                .padStart(2, "0")}`;

              return (
                <tr key={i} className="h-5 border-b">
                  <td className="text-xs pr-2 text-right w-20 text-gray-500 align-top">
                    {timeLabel}
                  </td>
                  {days.map((_, j) => (
                    <td
                      key={j}
                      className={`border-l cursor-pointer ${
                        isCellSelected(i, j) ? "bg-black" : "hover:bg-blue-50"
                      }`}
                      onMouseDown={() => handleMouseDown(i, j)}
                      onMouseEnter={() => handleMouseEnter(i, j)}
                      onMouseUp={() => handleMouseUp(i, j)}
                    />
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>

        {/* Bloques de actividades */}
        {actividades
          .filter((a) =>
            usuarioSeleccionado ? a.usuario_responsable?.id === parseInt(usuarioSeleccionado) : true
          )
          .map((actividad) => {
            const style = getStyleForActividad(actividad);
            return (
              <div
                key={actividad.id}
                onClick={() => {
                  setActividadEditando(actividad);
                  setFormPosition({ x: style.left, y: style.top });
                  setFormVisible(true);
                }}
                className="absolute bg-black text-white text-xs p-1 rounded cursor-pointer"
                style={{ ...style, width: 158 }}
              >
                {actividad.descripcion_publica}
              </div>
            );
          })}

        {formVisible && (
          <div
            className="absolute z-50"
            style={{ top: formPosition.y, left: formPosition.x }}
          >
            <NewCalendarActivity
              date={selection ? getDateFromSelection(selection) : null}
              onClose={() => {
                setFormVisible(false);
                setSelectedCells([]);
                setActividadEditando(null);
              }}
              usuarioSeleccionado={usuarioSeleccionado}
              actividad={actividadEditando}
            />
          </div>
        )}
      </div>
    </div>
  );
}
