// src/components/Calendar.jsx
import { useState, useRef } from "react";

const startHour = 6;
const endHour = 24;
const intervalMinutes = 15;
const days = ["Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado", "Domingo"];

export default function Calendar() {
  const [formVisible, setFormVisible] = useState(false);
  const [formPosition, setFormPosition] = useState({ x: 0, y: 0 });
  const [selection, setSelection] = useState(null);
  const calendarRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [startCell, setStartCell] = useState(null);

  const handleMouseDown = (rowIndex, colIndex) => {
    setIsDragging(true);
    setStartCell({ rowIndex, colIndex });
  };

  const handleMouseUp = (endRowIndex, endColIndex) => {
    if (isDragging && startCell) {
      const minRow = Math.min(startCell.rowIndex, endRowIndex);
      const maxRow = Math.max(startCell.rowIndex, endRowIndex);
      const col = startCell.colIndex; // se asume mismo día

      const top = minRow * 40 + 64; // header (4rem) + filtros (64px)
      const left = col * 160 + 160; // columna filtros + columnas días (aprox.)

      setFormPosition({ x: left, y: top });
      setSelection({ day: days[col], start: minRow, end: maxRow });
      setFormVisible(true);
    }
    setIsDragging(false);
    setStartCell(null);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormVisible(false);
    // Aquí se integraría con la lógica para guardar la actividad
  };

  const renderRows = () => {
    const rows = [];
    const totalRows = ((endHour - startHour) * 60) / intervalMinutes;
    for (let i = 0; i < totalRows; i++) {
      const hour = Math.floor(i * intervalMinutes / 60) + startHour;
      const minute = (i * intervalMinutes) % 60;
      const timeLabel = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
      rows.push(
        <tr key={i} className="h-10 border-b">
          <td className="text-xs pr-2 text-right align-top w-20 text-gray-500">{timeLabel}</td>
          {days.map((day, j) => (
            <td
              key={j}
              className="border-l cursor-pointer hover:bg-blue-50"
              onMouseDown={() => handleMouseDown(i, j)}
              onMouseUp={() => handleMouseUp(i, j)}
            />
          ))}
        </tr>
      );
    }
    return rows;
  };

  return (
    <div className="flex h-[calc(100vh-4rem)]">
      {/* Filtros (1/8) */}
      <div className="w-1/8 p-4 border-r bg-gray-50">
        <h3 className="font-semibold mb-2">Filtros</h3>
        <select className="w-full border rounded p-1 mb-2">
          <option>Usuario 1</option>
          <option>Usuario 2</option>
        </select>
        {/* Otros filtros */}
      </div>

      {/* Calendario */}
      <div className="flex-1 overflow-auto relative" ref={calendarRef}>
        <table className="w-full border-collapse">
          <thead className="sticky top-0 bg-white z-10">
            <tr>
              <th className="w-20" />
              {days.map((day, idx) => (
                <th key={idx} className="text-xs font-semibold text-gray-600 text-center py-2 border-l">
                  {day}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>{renderRows()}</tbody>
        </table>

        {formVisible && (
          <div
            className="absolute bg-white border rounded shadow-lg p-4 z-50"
            style={{ top: formPosition.y, left: formPosition.x, minWidth: "200px" }}
          >
            <form onSubmit={handleSubmit}>
              <h4 className="font-semibold mb-2">Nueva Actividad</h4>
              <input
                type="text"
                placeholder="Título"
                className="w-full border rounded p-1 mb-2"
              />
              <textarea
                placeholder="Descripción"
                className="w-full border rounded p-1 mb-2"
              ></textarea>
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={() => setFormVisible(false)}
                  className="text-sm px-3 py-1 border rounded hover:bg-gray-100"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="text-sm px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Crear
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}
