import { useState } from "react";

export default function NewActivityForm({ clienteId, clienteNombre, onCancel }) {
  const [tipo, setTipo] = useState("visita");
  const [descripcion, setDescripcion] = useState("");
  const [fecha, setFecha] = useState("");
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFin, setHoraFin] = useState("");
  const [estado, setEstado] = useState("pendiente");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (horaFin <= horaInicio) {
      setError("La hora de fin debe ser posterior a la hora de inicio.");
      return;
    }

    // Aquí iría la lógica de envío real de datos al backend

    console.log({
      clienteId,
      tipo,
      descripcion,
      fecha,
      horaInicio,
      horaFin,
      estado,
    });

    setError(""); // Borrar error si todo está bien
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="w-1/2 mx-auto bg-white p-6 rounded-lg shadow space-y-4"
    >
      <h2 className="text-xl font-semibold text-center">
        Nueva actividad para: <span className="text-blue-600">{clienteNombre}</span>
      </h2>

      {error && (
        <div className="text-red-600 text-sm bg-red-100 p-2 rounded border border-red-300">
          {error}
        </div>
      )}

      <div className="space-y-2">
        <label className="block text-sm font-medium">Tipo de actividad</label>
        <select
          value={tipo}
          onChange={(e) => setTipo(e.target.value)}
          className="w-full border rounded p-2"
        >
          <option value="visita">Visita</option>
          <option value="adquisicion">Adquisición</option>
          <option value="zona">Trabajo de zona</option>
          <option value="generica">Genérica</option>
          <option value="reunion">Reunión / Curso</option>
        </select>
      </div>

      <div className="flex gap-4">
        <div className="flex-1 space-y-2">
          <label className="block text-sm font-medium">Fecha</label>
          <input
            type="date"
            value={fecha}
            onChange={(e) => setFecha(e.target.value)}
            className="w-full border rounded p-2"
          />
        </div>

        <div className="flex-1 space-y-2">
          <label className="block text-sm font-medium">Hora inicio</label>
          <input
            type="time"
            value={horaInicio}
            onChange={(e) => setHoraInicio(e.target.value)}
            className="w-full border rounded p-2"
          />
        </div>

        <div className="flex-1 space-y-2">
          <label className="block text-sm font-medium">Hora fin</label>
          <input
            type="time"
            value={horaFin}
            onChange={(e) => setHoraFin(e.target.value)}
            className="w-full border rounded p-2"
          />
        </div>
      </div>

      <div className="space-y-2">
        <label className="block text-sm font-medium">Descripción</label>
        <textarea
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          className="w-full border rounded p-2"
          rows={3}
          maxLength={300}
        />
        <div className="text-right text-xs text-gray-500">
          {descripcion.length}/300 caracteres
        </div>
      </div>

      <div className="space-y-2">
        <label className="block text-sm font-medium">Estado</label>
        <select
          value={estado}
          onChange={(e) => setEstado(e.target.value)}
          className="w-full border rounded p-2"
        >
          <option value="pendiente">Pendiente</option>
          <option value="realizada">Realizada</option>
          <option value="no_realizada">No realizada</option>
        </select>
      </div>

      <div className="flex justify-between mt-6">
        <button
          type="button"
          onClick={onCancel}
          className="text-red-600 text-sm hover:underline"
        >
          Cancelar
        </button>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Guardar actividad
        </button>
      </div>
    </form>
  );
}
