import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { GripVertical, ChevronDown, ChevronUp } from "lucide-react";
import api from "../api";

export default function PedidoList() {
  const [pedidos, setPedidos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showEtiquetas, setShowEtiquetas] = useState(false);
  const [columnas, setColumnas] = useState([]);
  const navigate = useNavigate();

  // 沐ｹ Campos principales del modelo Pedido
  const todasLasColumnas = [
    { key: "cliente", label: "Cliente" },
    { key: "telefono", label: "Telﾃｩfono" },
    { key: "email", label: "Email" },
    { key: "ubicaciones", label: "Zonas de interﾃｩs" },
    { key: "precio_min", label: "Precio Mﾃｭnimo (竄ｬ)" },
    { key: "precio_max", label: "Precio Mﾃ｡ximo (竄ｬ)" },
    { key: "superficie_min", label: "Superficie Mﾃｭn (mﾂｲ)" },
    { key: "superficie_max", label: "Superficie Mﾃ｡x (mﾂｲ)" },
    { key: "habitaciones", label: "Dormitorios" },
    { key: "banos", label: "Baﾃｱos" },
    { key: "balcon", label: "Balcﾃｳn" },
    { key: "jardin", label: "Jardﾃｭn" },
    { key: "info_financiacion", label: "Financiaciﾃｳn" },
    { key: "fecha_ultima_modificacion", label: "ﾃ嗟tima modificaciﾃｳn" },
  ];

  // 沐ｹ Cargar pedidos desde API
  useEffect(() => {
    const fetchPedidos = async () => {
      setError(null);
      setLoading(true);
      try {
        const res = await api.get("/pedidos/");
        setPedidos(res.data);
      } catch (err) {
        console.error(err);
        if (err.response?.status === 401) {
          setError("Token invﾃ｡lido o expirado. Por favor, inicia sesiﾃｳn.");
        } else {
          setError("Error al cargar pedidos.");
        }
      } finally {
        setLoading(false);
      }
    };
    fetchPedidos();
  }, []);

  // 沐ｹ Inicializar columnas visibles
  useEffect(() => {
    setColumnas(
      todasLasColumnas.map((col) => ({
        ...col,
        visible: true,
      }))
    );
  }, []);

  // 沐ｹ Alternar visibilidad de columnas
  const handleToggleVisible = (key) => {
    setColumnas((prev) =>
      prev.map((col) =>
        col.key === key ? { ...col, visible: !col.visible } : col
      )
    );
  };

  // 沐ｹ Reordenar columnas
  const handleReorder = (index, direction) => {
    setColumnas((prev) => {
      const nueva = [...prev];
      const targetIndex = direction === "up" ? index - 1 : index + 1;
      if (targetIndex < 0 || targetIndex >= nueva.length) return nueva;
      [nueva[index], nueva[targetIndex]] = [nueva[targetIndex], nueva[index]];
      return nueva;
    });
  };

  if (loading) return <div>Cargando pedidos...</div>;

  if (error)
    return (
      <div className="text-red-600 p-4 bg-red-100 border border-red-300 rounded">
        {error}
      </div>
    );

  const columnasVisibles = columnas.filter((c) => c.visible);

  return (
    <div className="bg-white p-4 shadow rounded w-full">
      {/* Encabezado */}
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-bold">Solicitudes de Compra / Pedidos</h2>

        {/* Botﾃｳn de etiquetas */}
        <div className="relative w-1/4 min-w-[250px]">
          <button
            onClick={() => setShowEtiquetas(!showEtiquetas)}
            className="w-full flex justify-between items-center bg-gray-100 px-3 py-2 rounded border border-gray-300 hover:bg-gray-200"
          >
            <span className="font-medium text-gray-700">Etiquetas</span>
            {showEtiquetas ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
          </button>

          {showEtiquetas && (
            <div className="absolute top-full left-0 mt-2 w-full bg-white border border-gray-300 rounded shadow-lg max-h-72 overflow-auto z-10">
              {columnas.map((col, index) => (
                <div
                  key={col.key}
                  className="flex items-center justify-between px-2 py-1 hover:bg-gray-50"
                >
                  <div className="flex items-center gap-1">
                    <button
                      onClick={() => handleReorder(index, "up")}
                      className="p-1 hover:text-blue-600"
                      title="Mover arriba"
                    >
                      笆ｲ
                    </button>
                    <button
                      onClick={() => handleReorder(index, "down")}
                      className="p-1 hover:text-blue-600"
                      title="Mover abajo"
                    >
                      笆ｼ
                    </button>
                    <GripVertical size={16} className="text-gray-400" />
                    <span className="text-sm text-gray-700">{col.label}</span>
                  </div>
                  <input
                    type="checkbox"
                    checked={col.visible}
                    onChange={() => handleToggleVisible(col.key)}
                    className="h-4 w-4 accent-blue-600"
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Tabla dinﾃ｡mica */}
      <table className="w-full table-auto border-collapse text-sm">
        <thead>
          <tr className="bg-gray-200 text-left">
            {columnasVisibles.map((col) => (
              <th key={col.key} className="px-4 py-2">
                {col.label}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {pedidos.map((pedido) => (
            <tr
              key={pedido.id}
              className="hover:bg-gray-100 cursor-pointer"
              onClick={() => navigate(`/pedidos/${pedido.id}`)}
            >
              {columnasVisibles.map((col) => (
                <td key={col.key} className="px-4 py-2">
                  {renderCell(pedido, col.key)}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// 沐ｹ Renderizado de celdas segﾃｺn el tipo
