import { useState, useEffect } from "react";
import api from "../api";

export default function NewCalendarActivity({ date = null, onClose, usuarioSeleccionado, actividad = null }) {
  const [tipo, setTipo] = useState(actividad?.tipo || "Visita");
  const [estado, setEstado] = useState(actividad?.estado || "En proceso");
  const [descripcion, setDescripcion] = useState(actividad?.descripcion_empleado || "");
  const [descripcionPublica, setDescripcionPublica] = useState(actividad?.descripcion_publica || "");
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFin, setHoraFin] = useState("");
  const [fecha, setFecha] = useState(
    actividad?.fecha_inicio?.slice(0, 10) || date?.toISOString().slice(0, 10) || new Date().toISOString().slice(0, 10)
  );
  const [usuarioId, setUsuarioId] = useState(actividad?.usuario_responsable?.id || usuarioSeleccionado || null);
  const [usuarios, setUsuarios] = useState([]);
  const [error, setError] = useState(null);
  const [enviando, setEnviando] = useState(false);

  useEffect(() => {
    if (actividad) {
      const inicio = new Date(actividad.fecha_inicio);
      const fin = new Date(actividad.fecha_fin);
      setHoraInicio(inicio.toTimeString().slice(0, 5));
      setHoraFin(fin.toTimeString().slice(0, 5));
    } else {
      const now = new Date();
      now.setSeconds(0, 0);
      const roundedMinutes = Math.ceil(now.getMinutes() / 15) * 15;
      now.setMinutes(roundedMinutes === 60 ? 0 : roundedMinutes);
      if (roundedMinutes === 60) now.setHours(now.getHours() + 1);
      setHoraInicio(now.toTimeString().slice(0, 5));
      const fin = new Date(now.getTime() + 60 * 60000);
      setHoraFin(fin.toTimeString().slice(0, 5));
    }
  }, [actividad]);

  useEffect(() => {
    const fetchUsuarios = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await fetch("/api/usuarios/", {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
        if (!res.ok) return;
        const data = await res.json();
        setUsuarios(data);
      } catch (error) {
        console.error("Error al obtener usuarios:", error);
      }
    };

    fetchUsuarios();
  }, []);

  const validarHoras = () => horaInicio && horaFin && horaFin > horaInicio;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    if (!validarHoras()) return setError("La hora de fin debe ser mayor que la hora de inicio.");
    if (!usuarioId) return setError("Selecciona un usuario responsable.");

    setEnviando(true);

    const fechaInicioISO = new Date(`${fecha}T${horaInicio}:00`).toISOString();
    const fechaFinISO = new Date(`${fecha}T${horaFin}:00`).toISOString();

    const payload = {
      tipo,
      estado,
      descripcion_empleado: descripcion,
      descripcion_publica: estado === "Realizada" ? descripcionPublica : "Descripción aún no disponible",
      fecha_inicio: fechaInicioISO,
      fecha_fin: fechaFinISO,
      usuario_responsable: usuarioId,
    };

    try {
      const token = localStorage.getItem("accessToken");
      if (!token) throw new Error("No estás autenticado.");

      if (actividad?.id) {
        await api.put(`/actividades/${actividad.id}/`, payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await api.post("/actividades/", payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      onClose?.();
    } catch (err) {
      console.error("Error guardando actividad:", err);
      setError(err.response?.data ? JSON.stringify(err.response.data, null, 2) : err.message);
    } finally {
      setEnviando(false);
    }
  };

  return (
    <div className="bg-white border p-4 rounded shadow-lg w-[300px]">
      <h2 className="text-md font-semibold mb-2">
        {actividad ? "Editar Actividad" : "Nueva Actividad"}
      </h2>

      <select value={tipo} onChange={(e) => setTipo(e.target.value)} className="w-full mb-2 border p-1 rounded">
        <option value="Adquisición">Adquisición</option>
        <option value="Visita">Visita</option>
        <option value="Llamada">Llamada</option>
        <option value="Contacto directo">Contacto directo</option>
        <option value="Genérica">Genérica</option>
        <option value="Zona">Zona</option>
        <option value="Reunión o curso">Reunión o curso</option>
      </select>

      <textarea
        value={descripcion}
        onChange={(e) => e.target.value.length <= 1024 && setDescripcion(e.target.value)}
        rows={3}
        className="w-full border p-1 rounded mb-1"
        placeholder="Descripción interna"
      />

      <textarea
        value={descripcionPublica}
        onChange={(e) => estado === "Realizada" && setDescripcionPublica(e.target.value)}
        rows={2}
        className="w-full border p-1 rounded mb-1"
        placeholder="Descripción pública"
        disabled={estado !== "Realizada"}
      />

      <div className="flex gap-2 mb-2">
        <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} className="border p-1 rounded w-1/2" />
        <select value={horaInicio} onChange={(e) => setHoraInicio(e.target.value)} className="border p-1 rounded w-1/4">
          {generarOpcionesHoras()}
        </select>
        <select value={horaFin} onChange={(e) => setHoraFin(e.target.value)} className="border p-1 rounded w-1/4">
          {generarOpcionesHoras()}
        </select>
      </div>

      <select value={usuarioId || ""} onChange={(e) => setUsuarioId(Number(e.target.value))} className="w-full border p-1 rounded mb-2">
        <option value="">Seleccionar usuario</option>
        {usuarios.map((u) => (
          <option key={u.id} value={u.id}>
            {u.first_name || u.last_name ? `${u.first_name || ""} ${u.last_name || ""}`.trim() : u.username}
          </option>
        ))}
      </select>

      <select value={estado} onChange={(e) => setEstado(e.target.value)} className="w-full border p-1 rounded mb-2">
        <option value="Realizada">Realizada</option>
        <option value="En proceso">En proceso</option>
        <option value="No realizada">No realizada</option>
      </select>

      {error && <pre className="text-red-600 text-xs whitespace-pre-wrap">{error}</pre>}

      <div className="flex justify-end gap-2">
        <button onClick={onClose} className="text-sm px-3 py-1 bg-gray-300 rounded hover:bg-gray-400">Cancelar</button>
        <button
          onClick={handleSubmit}
          disabled={enviando}
          className={`text-sm px-3 py-1 text-white rounded ${enviando ? "bg-blue-300" : "bg-blue-600 hover:bg-blue-700"}`}
        >
          {actividad ? "Actualizar" : "Crear"}
        </button>
      </div>
    </div>
  );
}

function generarOpcionesHoras() {
  const opciones = [];
  for (let h = 0; h < 24; h++) {
    for (let m = 0; m < 60; m += 15) {
      const hh = h.toString().padStart(2, "0");
      const mm = m.toString().padStart(2, "0");
      opciones.push(<option key={`${hh}:${mm}`} value={`${hh}:${mm}`}>{`${hh}:${mm}`}</option>);
    }
  }
  return opciones;
}
