import { useState } from "react";

/**
 * Botón de guardado reutilizable con estados visuales y mensajes dinámicos.
 * 
 * Props:
 * - onSave: función async a ejecutar al pulsar guardar.
 * - label (opcional): texto por defecto del botón ("Guardar").
 * - successMessage (opcional): texto tras guardar con éxito ("Guardado ✅").
 * - errorMessage (opcional): texto tras error ("Error ❌").
 * - savingMessage (opcional): texto mientras guarda ("Guardando...").
 * - className (opcional): clases Tailwind adicionales.
 */
export default function SaveButton({
  onSave,
  label = "Guardar",
  successMessage = "Guardado ✅",
  errorMessage = "Error ❌",
  savingMessage = "Guardando...",
  className = "",
}) {
  const [status, setStatus] = useState("idle"); // idle | saving | success | error

  const handleClick = async () => {
    try {
      setStatus("saving");
      await onSave();
      setStatus("success");
      setTimeout(() => setStatus("idle"), 2000);
    } catch (err) {
      console.error("❌ Error en SaveButton:", err);
      setStatus("error");
      setTimeout(() => setStatus("idle"), 2000);
    }
  };

  const getButtonStyle = () => {
    switch (status) {
      case "saving":
        return "bg-blue-400 cursor-wait";
      case "success":
        return "bg-green-600 hover:bg-green-700";
      case "error":
        return "bg-red-600 hover:bg-red-700";
      default:
        return "bg-blue-600 hover:bg-blue-700";
    }
  };

  return (
    <button
      onClick={handleClick}
      disabled={status === "saving"}
      className={`px-3 py-1.5 rounded text-white transition flex items-center justify-center gap-2 ${getButtonStyle()} ${className}`}
    >
      {status === "saving"
        ? savingMessage
        : status === "success"
        ? successMessage
        : status === "error"
        ? errorMessage
        : label}
    </button>
  );
}
