import React, { useState } from "react";
import dayjs from "dayjs";

const NewCalendarActivity = ({ date, onClose }) => {
  const [formData, setFormData] = useState({
    tipo: "Visita",
    estado: "En proceso",
    fecha_inicio: dayjs(date).format("YYYY-MM-DDTHH:mm"),
    fecha_fin: dayjs(date).add(1, "hour").format("YYYY-MM-DDTHH:mm"),
    descripcion_empleado: "",
    descripcion_publica: "",
    cliente: "",
    inmueble: "",
    pedido: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const cleanData = (data) => {
    const parse = (v) => (v === "" ? null : isNaN(v) ? v : parseInt(v));
    return {
      ...data,
      cliente: parse(data.cliente),
      inmueble: parse(data.inmueble),
      pedido: parse(data.pedido),
    };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const cleanedData = cleanData(formData);

    console.log("Enviando datos:", cleanedData); //  Comprobaci贸n

    try {
      const response = await fetch("/api/actividades/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(cleanedData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Actividad creada:", data);
        onClose();
      } else {
        const err = await response.json();
        console.error("Error al guardar:", err);
        alert("Error al guardar la actividad. Revisa los datos.");
      }
    } catch (err) {
      console.error("Error de red:", err);
      alert("Fallo de conexi贸n al servidor.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-md">
        <h3 className="text-lg font-bold mb-4">Nueva Actividad</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <select name="tipo" value={formData.tipo} onChange={handleChange} className="w-full border p-2">
            <option value="Adquisici贸n">Adquisici贸n</option>
            <option value="Visita">Visita</option>
            <option value="Llamada">Llamada</option>
            <option value="Contacto directo">Contacto directo</option>
          </select>

          <select name="estado" value={formData.estado} onChange={handleChange} className="w-full border p-2">
            <option value="Realizada">Realizada</option>
            <option value="No realizada">No realizada</option>
            <option value="En proceso">En proceso</option>
          </select>

          <input
            type="datetime-local"
            name="fecha_inicio"
            value={formData.fecha_inicio}
            onChange={handleChange}
            className="w-full border p-2"
          />

          <input
            type="datetime-local"
            name="fecha_fin"
            value={formData.fecha_fin}
            onChange={handleChange}
            className="w-full border p-2"
          />

          <textarea
            name="descripcion_empleado"
            value={formData.descripcion_empleado}
            onChange={handleChange}
            placeholder="Descripci贸n para el equipo"
            className="w-full border p-2"
          />

          <textarea
            name="descripcion_publica"
            value={formData.descripcion_publica}
            onChange={handleChange}
            placeholder="Descripci贸n p煤blica"
            className="w-full border p-2"
          />

          <input
            type="text"
            name="cliente"
            value={formData.cliente}
            onChange={handleChange}
            placeholder="ID Cliente (opcional)"
            className="w-full border p-2"
          />

          <input
            type="text"
            name="inmueble"
            value={formData.inmueble}
            onChange={handleChange}
            placeholder="ID Inmueble (opcional)"
            className="w-full border p-2"
          />

          <input
            type="text"
            name="pedido"
            value={formData.pedido}
            onChange={handleChange}
            placeholder="ID Pedido (opcional)"
            className="w-full border p-2"
          />

          <div className="flex justify-end gap-2">
            <button type="button" onClick={onClose} className="bg-gray-300 px-4 py-2 rounded">
              Cancelar
            </button>
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
              Guardar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewCalendarActivity;
