import React, { useState, useContext } from 'react';
import { login as loginApi } from './api/auth';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';

export default function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const { login } = useContext(AuthContext); // ✅ usamos login del context

  async function handleSubmit(e) {
    e.preventDefault();
    setError(null);

    try {
      const data = await loginApi(username, password); // llamamos a API
      login(data); // ✅ actualizamos el contexto
      navigate('/'); // redirigimos como siempre
    } catch (err) {
      console.error('Error de login:', err);
      setError('Credenciales inválidas');
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded shadow-md w-80"
      >
        <h2 className="text-2xl font-bold mb-6 text-center">Iniciar sesión</h2>

        <input
          type="text"
          placeholder="Usuario"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          className="w-full border border-gray-300 p-2 mb-4 rounded"
        />

        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="w-full border border-gray-300 p-2 mb-4 rounded"
        />

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
        >
          Entrar
        </button>

        {error && (
          <p className="text-red-600 mt-4 text-sm text-center">{error}</p>
        )}
      </form>
    </div>
  );
}
