import { useEffect, useState } from "react";
import api from "../api";
import { useNavigate } from "react-router-dom";

export default function NewActivityForm({ clienteId = null, clienteNombre = "", onCreated = null }) {
  const [tipo, setTipo] = useState("Visita");
  const [estado, setEstado] = useState("En proceso");
  const [descripcion, setDescripcion] = useState("");
  const [fecha, setFecha] = useState(new Date().toISOString().split("T")[0]);
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFin, setHoraFin] = useState("");
  const [usuarios, setUsuarios] = useState([]);
  const [usuarioId, setUsuarioId] = useState(null);
  const [error, setError] = useState(null);
  const [enviando, setEnviando] = useState(false);

  const navigate = useNavigate();

  // Genera intervalos de 15 min
  const generarHoras = () => {
    const horas = [];
    for (let h = 0; h < 24; h++) {
      for (let m = 0; m < 60; m += 15) {
        const hh = h.toString().padStart(2, "0");
        const mm = m.toString().padStart(2, "0");
        horas.push(`${hh}:${mm}`);
      }
    }
    return horas;
  };

  const horas = generarHoras();

  useEffect(() => {
    // Hora actual más próxima
    const ahora = new Date();
    const minutos = Math.ceil(ahora.getMinutes() / 15) * 15;
    ahora.setMinutes(minutos);
    setHoraInicio(ahora.toTimeString().slice(0, 5));
    const antes = new Date(ahora.getTime() - 60 * 60 * 1000);
    setHoraFin(antes.toTimeString().slice(0, 5));
  }, []);

  useEffect(() => {
    // Cargar usuarios del sistema
    const fetchUsuarios = async () => {
      try {
        const res = await api.get("/usuarios/");
        setUsuarios(res.data);
        if (res.data.length > 0) setUsuarioId(res.data[0].id);
      } catch (err) {
        console.error("Error al cargar usuarios");
      }
    };
    fetchUsuarios();
  }, []);

  const caracteresRestantes = 1024 - descripcion.length;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setEnviando(true);

    const fecha_inicio = `${fecha}T${horaInicio}:00`;
    const fecha_fin = `${fecha}T${horaFin}:00`;

    const payload = {
      tipo,
      estado,
      descripcion_empleado: descripcion,
      descripcion_publica: "",
      fecha_inicio,
      fecha_fin,
      cliente: clienteId || null,
      usuario_responsable: usuarioId,
    };

    try {
      const token = localStorage.getItem("access_token");
      if (!token) throw new Error("No estás autenticado.");

      await api.post("/actividades/", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      onCreated ? onCreated() : navigate(-1);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || "Error al crear la actividad.");
    } finally {
      setEnviando(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="w-1/4 mx-auto mt-8 bg-white p-6 rounded shadow space-y-4"
    >
      <div className="flex justify-between items-center">
        <div className="text-sm font-medium bg-gray-100 px-3 py-2 rounded">
          Cliente: <span className="font-semibold">{clienteNombre}</span>
        </div>

        <select
          value={tipo}
          onChange={(e) => setTipo(e.target.value)}
          className="border rounded px-3 py-2"
        >
          <option value="Adquisición">Adquisición</option>
          <option value="Visita">Visita</option>
          <option value="Llamada">Llamada</option>
          <option value="Contacto directo">Contacto directo</option>
        </select>
      </div>

      <div>
        <label className="text-sm font-medium">Descripción</label>
        <textarea
          maxLength={1024}
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          rows={4}
          className="w-full mt-1 border rounded px-3 py-2"
        />
        <p className="text-right text-xs text-gray-500">{caracteresRestantes} caracteres restantes</p>
      </div>

      <div className="flex gap-4">
        <div className="flex-1">
          <label className="text-sm font-medium">Hora de inicio</label>
          <select
            value={horaInicio}
            onChange={(e) => setHoraInicio(e.target.value)}
            className="w-full mt-1 border rounded px-3 py-2"
          >
            {horas.map((h) => (
              <option key={h} value={h}>{h}</option>
            ))}
          </select>
        </div>

        <div className="flex-1">
          <label className="text-sm font-medium">Hora de fin</label>
          <select
            value={horaFin}
            onChange={(e) => setHoraFin(e.target.value)}
            className="w-full mt-1 border rounded px-3 py-2"
          >
            {horas.map((h) => (
              <option key={h} value={h}>{h}</option>
            ))}
          </select>
        </div>
      </div>

      <div>
        <label className="text-sm font-medium">Fecha</label>
        <input
          type="date"
          value={fecha}
          onChange={(e) => setFecha(e.target.value)}
          className="w-full mt-1 border rounded px-3 py-2"
        />
      </div>

      <div className="flex gap-4">
        <div className="flex-1">
          <label className="text-sm font-medium">Usuario responsable</label>
          <select
            value={usuarioId || ""}
            onChange={(e) => setUsuarioId(e.target.value)}
            className="w-full mt-1 border rounded px-3 py-2"
          >
            {usuarios.map((u) => (
              <option key={u.id} value={u.id}>{u.username}</option>
            ))}
          </select>
        </div>

        <div className="flex-1">
          <label className="text-sm font-medium">Estado</label>
          <select
            value={estado}
            onChange={(e) => setEstado(e.target.value)}
            className="w-full mt-1 border rounded px-3 py-2"
          >
            <option value="Realizada">Realizada</option>
            <option value="No realizada">No realizada</option>
            <option value="En proceso">En proceso</option>
          </select>
        </div>
      </div>

      {error && <p className="text-red-600 text-sm">{error}</p>}

      <div className="flex justify-end gap-3 mt-4">
        <button
          type="button"
          onClick={() => navigate(-1)}
          className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400"
        >
          Cancelar
        </button>
        <button
          type="submit"
          disabled={enviando}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {enviando ? "Guardando..." : "Guardar"}
        </button>
      </div>
    </form>
  );
}
