import { useEffect, useState } from "react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";

const today = new Date();

export default function MonthView({ usuarioSeleccionado }) {
  const [fechaBase, setFechaBase] = useState(new Date(today.getFullYear(), today.getMonth(), 1));
  const [actividades, setActividades] = useState([]);
  const [formVisible, setFormVisible] = useState(false);
  const [actividadEditando, setActividadEditando] = useState(null);
  const [fechaSeleccionada, setFechaSeleccionada] = useState(null);

  useEffect(() => {
    const fetchActividades = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await api.get("/actividades/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setActividades(res.data);
      } catch (err) {
        console.error("Error obteniendo actividades:", err);
      }
    };

    fetchActividades();
  }, [fechaBase, formVisible]);

  const diasMes = () => {
    const year = fechaBase.getFullYear();
    const month = fechaBase.getMonth();
    const startDate = new Date(year, month, 1);
    const endDate = new Date(year, month + 1, 0);
    const startDay = startDate.getDay() === 0 ? 6 : startDate.getDay() - 1; // lunes = 0
    const totalDays = endDate.getDate();
    const days = [];

    for (let i = 0; i < startDay; i++) {
      days.push(null);
    }

    for (let d = 1; d <= totalDays; d++) {
      days.push(new Date(year, month, d));
    }

    return days;
  };

  const actividadesDelDia = (fecha) => {
    return actividades.filter((a) => {
      const actDate = new Date(a.fecha_inicio);
      return (
        actDate.toDateString() === fecha.toDateString() &&
        (!usuarioSeleccionado || a.usuario_responsable?.id === parseInt(usuarioSeleccionado))
      );
    });
  };

  const cambiarMes = (delta) => {
    const nuevaFecha = new Date(fechaBase);
    nuevaFecha.setMonth(nuevaFecha.getMonth() + delta);
    setFechaBase(nuevaFecha);
  };

  const nombreMes = fechaBase.toLocaleString("es-ES", {
    month: "long",
    year: "numeric",
  });

  return (
    <div className="flex flex-col h-full">
      <div className="flex justify-between items-center p-4 bg-gray-50 border-b">
        <button
          onClick={() => cambiarMes(-1)}
          className="text-sm px-3 py-1 rounded bg-gray-200 hover:bg-gray-300"
        >
          ← Anterior
        </button>
        <h2 className="text-lg font-semibold capitalize">{nombreMes}</h2>
        <button
          onClick={() => cambiarMes(1)}
          className="text-sm px-3 py-1 rounded bg-gray-200 hover:bg-gray-300"
        >
          Siguiente →
        </button>
      </div>

      <div className="grid grid-cols-7 gap-px bg-gray-200 flex-1">
        {["Lun", "Mar", "Mié", "Jue", "Vie", "Sáb", "Dom"].map((d) => (
          <div key={d} className="bg-white p-2 text-xs font-bold border-b">
            {d}
          </div>
        ))}

        {diasMes().map((fecha, i) => (
          <div
            key={i}
            className={`relative bg-white min-h-[100px] p-1 border ${
              fecha?.toDateString() === today.toDateString()
                ? "border-blue-600"
                : "border-gray-200"
            }`}
            onClick={() => {
              if (!fecha) return;
              setFechaSeleccionada(fecha);
              setActividadEditando(null);
              setFormVisible(true);
            }}
          >
            {fecha && (
              <div className="text-xs font-semibold text-gray-700">
                {fecha.getDate()}
              </div>
            )}

            {fecha &&
              actividadesDelDia(fecha).map((actividad) => (
                <div
                  key={actividad.id}
                  onClick={(e) => {
                    e.stopPropagation();
                    setActividadEditando(actividad);
                    setFechaSeleccionada(null);
                    setFormVisible(true);
                  }}
                  className="mt-1 text-[11px] text-white bg-black rounded px-1 py-[2px] cursor-pointer"
                >
                  {actividad.descripcion_publica}
                </div>
              ))}
          </div>
        ))}
      </div>

      {formVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50">
          <NewActivityForm
            actividad={actividadEditando}
            clienteId={actividadEditando?.cliente?.id || null}
            inmuebleId={actividadEditando?.inmueble?.id || null}
            onCreated={() => {
              setFormVisible(false);
              setActividadEditando(null);
              setFechaSeleccionada(null);
            }}
            onCancel={() => {
              setFormVisible(false);
              setActividadEditando(null);
              setFechaSeleccionada(null);
            }}
            // si es nueva, predefinir fecha
            date={fechaSeleccionada}
          />
        </div>
      )}
    </div>
  );
}
