import { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function PropertyMap({ latitud, longitud, direccion }) {
  useEffect(() => {
    const map = L.map("property-map").setView([latitud || 0, longitud || 0], 16);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    if (latitud && longitud) {
      L.marker([latitud, longitud])
        .addTo(map)
        .bindPopup(`<b>${direccion || "Ubicación del inmueble"}</b>`)
        .openPopup();
    }

    return () => {
      map.remove();
    };
  }, [latitud, longitud, direccion]);

  return (
    <div
      id="property-map"
      className="w-full h-48 rounded shadow border"
      style={{ minHeight: "200px" }}
    />
  );
}
