import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Pencil, ChevronDown } from "lucide-react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";

export default function PropertyDetail() {
  const { id } = useParams();
  const [inmueble, setInmueble] = useState(null);
  const [actividades, setActividades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchInmueble = async () => {
    try {
      const res = await api.get(`/inmuebles/${id}/`);
      setInmueble(res.data);
    } catch (err) {
      console.error(err);
      if (err.response?.status === 401) {
        setError("Token inválido o expirado. Por favor, inicia sesión.");
      } else {
        setError("Error al cargar el inmueble.");
      }
    }
  };

  const fetchActividades = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const res = await api.get(`/actividades/inmueble/${id}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setActividades(res.data);
    } catch (err) {
      console.error("Error cargando actividades del inmueble:", err);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      setError(null);
      setLoading(true);
      try {
        await fetchInmueble();
        await fetchActividades();
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  if (loading) return <div>Cargando inmueble y actividades...</div>;

  if (error)
    return (
      <div className="text-red-600 p-4 bg-red-100 border border-red-300 rounded">
        <p className="font-bold">Error al cargar los datos:</p>
        <p>{error}</p>
      </div>
    );

  if (!inmueble) return <div>Datos del inmueble vacíos o mal formateados.</div>;

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("es-ES") : "—";

  return (
    <div className="flex gap-4 w-full relative">
      {showForm && (
        <div className="absolute inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg shadow-lg p-6 w-[40rem] max-w-full relative">
            <button
              onClick={() => setShowForm(false)}
              className="absolute top-2 right-2 text-gray-600 hover:text-black text-sm"
              aria-label="Cerrar formulario"
            >
              ✕
            </button>
            <NewActivityForm
              inmuebleId={id}
              onCancel={() => setShowForm(false)}
              onCreated={async () => {
                setShowForm(false);
                await fetchActividades();
              }}
            />
          </div>
        </div>
      )}

      {/* Sección izquierda */}
      <div className="w-2/3 space-y-4">
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock label="Último contacto" value={formatDate(inmueble.fecha_ultimo_contacto)} />
          <InfoBlock label="Ocupado por" value={inmueble.ocupado_por || "—"} />
          <InfoBlock label="Actividades" value={actividades.length} />
          <InfoBlock label="Última venta/alquiler" value={formatDate(inmueble.fecha_ultima_venta_alquiler)} />
          <InfoBlock label="Última modificación" value={formatDate(inmueble.fecha_ultima_modificacion)} />
          <InfoBlock label="Zona asignada" value={inmueble.zona_asignada || "—"} />
        </div>

        <div className="flex gap-4">
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Planta" value={inmueble.planta} />
            <EditableSection label="Puerta" value={inmueble.puerta} />
            <EditableSection label="Interior" value={inmueble.interior || "—"} />
            <EditableSection label="Referencia Catastral" value={inmueble.ref_catastral || "—"} />
            <EditableSection label="Certificado Energético" value={inmueble.estado_certificado || "—"} />
            <EditableSection label="Consumo Energía" value={inmueble.consumo_energia ?? "—"} />
            <EditableSection label="Emisiones CO2" value={inmueble.emisiones_CO2 ?? "—"} />
            <EditableSection label="Nota Simple" value={inmueble.nota_simple ? "Sí" : "No"} />
          </div>
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Habitaciones" value={inmueble.habitaciones} />
            <EditableSection label="Baños" value={inmueble.banos} />
            <EditableSection label="Estancias" value={inmueble.estancias} />
            <EditableSection label="Balcón" value={inmueble.balcon ? "Sí" : "No"} />
            <EditableSection label="Jardín" value={inmueble.jardin ? "Sí" : "No"} />
            <EditableSection label="Día de Zona" value={formatDate(inmueble.dia_zona)} />
            <EditableSection label="ID Edificio" value={inmueble.edificio_id || "—"} />
            <EditableSection label="ID Inquilino" value={inmueble.inquilino_id || "—"} />
            <EditableSection label="ID Propietario" value={inmueble.propietario_id || "—"} />
          </div>
        </div>
      </div>

      {/* Sección derecha */}
      <div className="w-1/3 space-y-4">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold">Actividades y Citas</h3>
          <button
            onClick={() => setShowForm(true)}
            className="text-sm text-blue-600 hover:underline"
          >
            + Nueva
          </button>
        </div>

        {actividades.length === 0 ? (
          <p>No hay actividades registradas.</p>
        ) : (
          actividades.map((actividad) => (
            <ActividadCard key={actividad.id || actividad.fecha_inicio} actividad={actividad} />
          ))
        )}

        <button className="text-sm text-blue-600 hover:underline">Ver más</button>
      </div>
    </div>
  );
}

function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableSection({ label, value }) {
  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      <p className="text-sm">{value}</p>
      <button className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition">
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}

function ActividadCard({ actividad }) {
  return (
    <div className="bg-white shadow p-4 rounded border border-gray-200 hover:border-blue-500 space-y-2">
      <div className="flex justify-between items-center">
        <p className="text-sm font-semibold text-gray-700">{actividad.tipo || "—"}</p>
        <div className="flex items-center gap-1 text-sm text-gray-500">
          <span>
            {actividad.fecha_inicio
              ? new Date(actividad.fecha_inicio).toLocaleDateString("es-ES")
              : "—"}
          </span>
          <button>
            <ChevronDown size={14} />
          </button>
        </div>
      </div>

      <p className="text-sm font-medium">
        {actividad.inmueble?.ref_catastral || "Inmueble"}
      </p>

      <div className="border border-blue-600 rounded p-2 mt-[0.625rem] text-sm">
        <div className="flex justify-between">
          <div>
            <p className="text-gray-500">Agencia</p>
            <p>Falcon Group - Tarragona</p>
          </div>
          <div>
            <p className="text-gray-500">Responsable</p>
            <p>{actividad.creado_por?.username || "Usuario no disponible"}</p>
          </div>
        </div>
      </div>

      <div>
        <p className="text-sm text-gray-500 mt-2">Descripción</p>
        <p className="text-sm">{actividad.descripcion_empleado || "Sin descripción"}</p>
      </div>
    </div>
  );
}
