// Calendar.jsx
import React, { useEffect, useState } from "react";
import dayjs from "dayjs";
import NewCalendarActivity from "./NewCalendarActivity";

const Calendar = () => {
  const [activities, setActivities] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [showNewActivity, setShowNewActivity] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(dayjs());

  const fetchActivities = async () => {
    try {
      const response = await fetch("/api/actividades/?limit=200");
      const data = await response.json();
      setActivities(data);
    } catch (error) {
      console.error("Error fetching activities:", error);
    }
  };

  useEffect(() => {
    fetchActivities();
  }, []);

  const generateCalendar = () => {
    const startOfMonth = currentMonth.startOf("month").startOf("week");
    const endOfMonth = currentMonth.endOf("month").endOf("week");
    const date = startOfMonth.clone();
    const weeks = [];

    while (date.isBefore(endOfMonth, "day")) {
      const days = [];
      for (let i = 0; i < 7; i++) {
        const day = date.clone();
        const dayActivities = activities.filter((activity) =>
          dayjs(activity.fecha_inicio).isSame(day, "day")
        );

        days.push(
          <div
            key={day.toString()}
            className="border h-24 w-full p-1 cursor-pointer hover:bg-gray-100"
            onClick={() => {
              setSelectedDate(day);
              setShowNewActivity(true);
            }}
          >
            <div className="text-xs font-bold">{day.date()}</div>
            <div className="space-y-1 overflow-hidden overflow-y-auto max-h-16">
              {dayActivities.map((act) => (
                <div
                  key={act.id}
                  className="text-xs bg-blue-100 text-blue-800 rounded px-1"
                >
                  {act.tipo}: {act.descripcion_empleado.slice(0, 15)}
                </div>
              ))}
            </div>
          </div>
        );
        date.add(1, "day");
      }
      weeks.push(
        <div key={date.toString()} className="flex">
          {days}
        </div>
      );
    }
    return weeks;
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">
        {currentMonth.format("MMMM YYYY")}
      </h2>
      <div className="grid grid-cols-7 text-center font-semibold">
        {["Lun", "Mar", "Mié", "Jue", "Vie", "Sáb", "Dom"].map((d) => (
          <div key={d}>{d}</div>
        ))}
      </div>
      <div>{generateCalendar()}</div>

      {showNewActivity && (
        <NewCalendarActivity
          date={selectedDate}
          onClose={() => {
            setShowNewActivity(false);
            fetchActivities();
          }}
        />
      )}
    </div>
  );
};

export default Calendar;
