// src/components/NewCalendarActivity.jsx
import { useState, useEffect } from "react";

export default function NewCalendarActivity({ position, onClose, selection }) {
  const [formData, setFormData] = useState({
    tipo: "visita",
    inicio: "",
    fin: "",
    usuario: "",
    descripcion: "",
    cliente: "",
    estado: "pendiente"
  });

  useEffect(() => {
    if (selection) {
      const getHourMinute = (row) => {
        const hour = Math.floor(row * 15 / 60) + 6;
        const minute = (row * 15) % 60;
        return `${hour.toString().padStart(2, "0")}:${minute.toString().padStart(2, "0")}`;
      };

      const hoy = new Date();
      const dayIndex = ["Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado", "Domingo"].indexOf(selection.day);
      const currentMonday = new Date(hoy.setDate(hoy.getDate() - hoy.getDay() + 1));
      const fechaSeleccionada = new Date(currentMonday);
      fechaSeleccionada.setDate(currentMonday.getDate() + dayIndex);

      const fechaBase = fechaSeleccionada.toISOString().split("T")[0];

      setFormData((prev) => ({
        ...prev,
        inicio: `${fechaBase}T${getHourMinute(selection.start)}`,
        fin: `${fechaBase}T${getHourMinute(selection.end)}`
      }));
    }
  }, [selection]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // lógica de guardado de actividad
    onClose();
  };

  return (
    <div
      className="absolute bg-white border rounded shadow-lg p-4 z-50"
      style={{ top: position.y, left: position.x, minWidth: "250px" }}
    >
      <form onSubmit={handleSubmit}>
        <h4 className="font-semibold mb-2">Nueva Actividad</h4>

        <label className="block mb-2 text-sm">
          Tipo:
          <select name="tipo" value={formData.tipo} onChange={handleChange} className="w-full border rounded p-1">
            <option value="visita">Visita</option>
            <option value="adquisicion">Adquisición</option>
            <option value="zona">Zona</option>
            <option value="generica">Genérica</option>
            <option value="reunion">Reunión/Curso</option>
          </select>
        </label>

        <label className="block mb-2 text-sm">
          Inicio:
          <input type="datetime-local" name="inicio" value={formData.inicio} onChange={handleChange} className="w-full border rounded p-1" />
        </label>

        <label className="block mb-2 text-sm">
          Fin:
          <input type="datetime-local" name="fin" value={formData.fin} onChange={handleChange} className="w-full border rounded p-1" />
        </label>

        <label className="block mb-2 text-sm">
          Usuario:
          <input type="text" name="usuario" value={formData.usuario} onChange={handleChange} className="w-full border rounded p-1" />
        </label>

        <label className="block mb-2 text-sm">
          Cliente:
          <input type="text" name="cliente" value={formData.cliente} onChange={handleChange} className="w-full border rounded p-1" />
        </label>

        <label className="block mb-2 text-sm">
          Estado:
          <select name="estado" value={formData.estado} onChange={handleChange} className="w-full border rounded p-1">
            <option value="realizada">Realizada</option>
            <option value="no_realizada">No realizada</option>
            <option value="pendiente">Pendiente</option>
          </select>
        </label>

        <label className="block mb-2 text-sm">
          Descripción:
          <textarea name="descripcion" value={formData.descripcion} onChange={handleChange} className="w-full border rounded p-1" />
        </label>

        <div className="flex justify-end space-x-2 mt-2">
          <button
            type="button"
            onClick={onClose}
            className="text-sm px-3 py-1 border rounded hover:bg-gray-100"
          >
            Cancelar
          </button>
          <button
            type="submit"
            className="text-sm px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Crear
          </button>
        </div>
      </form>
    </div>
  );
}
