import { useEffect, useState } from "react";
import api from "../api";
import { Pencil, ChevronDown } from "lucide-react";
import NewActivityForm from "./NewActivityForm";
import { Link, useParams } from "react-router-dom";
import NewPedidoForm from "./NewPedidoForm";


export default function ClientDetail() {
  const { id } = useParams();
  const [cliente, setCliente] = useState(null);
  const [actividades, setActividades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [showPedidoForm, setShowPedidoForm] = useState(false); // âœ… NUEVO estado para pedidos
  

  const fetchCliente = async () => {
    const token = localStorage.getItem("accessToken");
    if (!token) throw new Error("No estÃ¡s autenticado. Por favor, inicia sesiÃ³n.");
    const res = await api.get(`/clientes/${id}/`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setCliente(res.data);
  };

  const fetchActividades = async () => {
    const token = localStorage.getItem("accessToken");
    if (!token) throw new Error("No estÃ¡s autenticado. Por favor, inicia sesiÃ³n.");
    const res = await api.get(`/actividades/cliente/${id}/`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setActividades(res.data);
  };

  useEffect(() => {
    const fetchData = async () => {
      setError(null);
      setLoading(true);
      try {
        await fetchCliente();
        await fetchActividades();
      } catch (err) {
        console.error(err);
        if (err.response?.status === 404) {
          setError("No se encontrÃ³ el cliente con este ID.");
        } else if (err.response?.status === 401) {
          setError("Token invÃ¡lido o expirado. Por favor, inicia sesiÃ³n de nuevo.");
        } else {
          setError(err.response?.data?.detail || err.message || "Error al cargar los datos.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return <div>Cargando cliente y actividades...</div>;

  if (error)
    return (
      <div className="text-red-600 p-4 bg-red-100 border border-red-300 rounded">
        <p className="font-bold">Error al cargar los datos:</p>
        <p>{error}</p>
      </div>
    );

  if (!cliente) return <div>Datos del cliente vacÃ­os o mal formateados.</div>;

  return (
    <div className="flex gap-4 w-full relative">
      {/* MODAL: NUEVA ACTIVIDAD */}
      {showForm && (
        <div className="absolute inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg shadow-lg p-6 w-[40rem] max-w-full relative">
            <button
              onClick={() => setShowForm(false)}
              className="absolute top-2 right-2 text-gray-600 hover:text-black text-sm"
              aria-label="Cerrar formulario"
            >
              âœ•
            </button>
            <NewActivityForm
              clienteId={id}
              clienteNombre={cliente.nombre}
              clienteApellido1={cliente.apellido1}
              clienteApellido2={cliente.apellido2}
              onCancel={() => setShowForm(false)}
              onCreated={async () => {
                setShowForm(false);
                try {
                  await fetchActividades();
                } catch (err) {
                  console.error("Error recargando actividades:", err);
                }
              }}
            />
          </div>
        </div>
      )}

      {/* MODAL: NUEVO PEDIDO âœ… */}
      {showPedidoForm && (
        <div className="absolute inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg shadow-lg p-6 w-[40rem] max-w-full relative">
            <button
              onClick={() => setShowPedidoForm(false)}
              className="absolute top-2 right-2 text-gray-600 hover:text-black text-sm"
            >
              âœ•
            </button>
            <NewPedidoForm
              clienteId={cliente.id}
              onCancel={() => setShowPedidoForm(false)}
              onCreated={async () => {
                setShowPedidoForm(false);
                await fetchCliente();
              }}
            />
          </div>
        </div>
      )}

      {/* SecciÃ³n izquierda */}
      <div className="w-2/3 space-y-4">
        {/* Panel superior con informaciÃ³n clave */}
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock label="Ãšltimo contacto" value={cliente.ultimo_contacto || "â€”"} />
          <InfoBlock label="Pedidos" value={cliente.pedidos?.length || 0} />
          <InfoBlock label="Inmuebles" value={cliente.inmuebles?.length || 0} />
          <InfoBlock label="InterÃ©s" value={cliente.interes || "â€”"} />
          <InfoBlock label="Actividades" value={actividades.length} />
          <InfoBlock label="DÃ­as desde contacto" value={cliente.dias_ultimo_contacto || "â€”"} />
        </div>

        {/* InformaciÃ³n general del cliente */}
        <div className="flex gap-4">
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Nombre" value={cliente.nombre || "â€”"} />
            <EditableSection label="Primer apellido" value={cliente.apellido1 || "â€”"} />
            <EditableSection label="Segundo apellido" value={cliente.apellido2 || "â€”"} />
            <EditableSection label="DirecciÃ³n" value={cliente.direccion || "â€”"} />
            <EditableSection label="PoblaciÃ³n" value={cliente.poblacion || "â€”"} />
            <EditableSection label="Tipo Documento" value={cliente.tipo_documento || "â€”"} />
            <EditableSection label="NÂº IdentificaciÃ³n" value={cliente.num_identificacion || "â€”"} />
            <EditableSection label="Trato" value={cliente.trato || "â€”"} />
            <EditableSection label="Sexo" value={cliente.sexo || "â€”"} />
          </div>

          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="TelÃ©fono" value={cliente.telefono || "â€”"} />
            <EditableSection label="MÃ³vil" value={cliente.telefono_movil || "â€”"} />
            <EditableSection label="Email" value={cliente.email || "â€”"} />
            <EditableSection label="Email secundario" value={cliente.email_secundario || "â€”"} />
            <EditableSection label="InformaciÃ³n adicional" value={cliente.info_adicional || "â€”"} />
            <EditableSection label="DÃ­as desde Ãºltimo contacto" value={cliente.dias_ultimo_contacto || "â€”"} />
            <EditableSection label="InterÃ©s" value={cliente.interes || "â€”"} />
            <EditableSection
              label="Fecha Ãºltima modificaciÃ³n"
              value={new Date(cliente.fecha_ultima_modificacion).toLocaleString("es-ES")}
            />
          </div>
        </div>
      </div>

      {/* SecciÃ³n derecha */}
      <div className="w-1/3 space-y-4">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold">Actividades y Citas</h3>
          <button
            onClick={() => setShowForm(true)}
            className="text-sm text-blue-600 hover:underline"
          >
            + Nueva
          </button>
        </div>

        {actividades.length === 0 ? (
          <p>No hay actividades registradas.</p>
        ) : (
          actividades.map((actividad) => (
            <ActividadCard key={actividad.id || actividad.fecha_inicio} actividad={actividad} />
          ))
        )}

        <button className="text-sm text-blue-600 hover:underline">Ver mÃ¡s</button>

        {/* Inmuebles relacionados */}
        {(() => {
          const inmueblesRelacionados = [
            ...(cliente.propiedades || []),
            ...(cliente.alquileres || []),
            ...(cliente.copropiedades || []),
            ...(cliente.coalquileres || []),
          ];

          return inmueblesRelacionados.length > 0 ? (
            <div className="bg-white rounded shadow p-3 mt-4 w-full">
              <h3 className="text-lg font-semibold mb-2">Inmuebles relacionados</h3>
              <div className="space-y-2">
                {inmueblesRelacionados.map((inmueble) => (
                  <div
                    key={inmueble.id}
                    className="border border-gray-200 rounded p-2 hover:border-blue-400 transition"
                  >
                    <Link
                      to={`/inmuebles/${inmueble.id}`}
                      className="font-semibold text-gray-800 hover:text-blue-600 transition-colors"
                    >
                      {inmueble.edificio?.calle || "Calle desconocida"}{" "}
                      {inmueble.edificio?.numero_calle || ""}
                    </Link>

                    <p className="text-sm text-gray-600">
                      Planta {inmueble.planta || "â€”"}, Puerta {inmueble.puerta || "â€”"}
                    </p>
                    <p className="text-sm text-gray-600">
                      Ref. Catastral: {inmueble.ref_catastral || "â€”"}
                    </p>
                    <p className="text-sm text-gray-600">
                      Estado CRM: {inmueble.estado_CRM || "â€”"}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="bg-white rounded shadow p-3 mt-4 w-full">
              <h3 className="text-lg font-semibold mb-2">Inmuebles relacionados</h3>
              <p className="text-sm text-gray-600">
                No hay inmuebles relacionados con este cliente.
              </p>
            </div>
          );
        })()}

        {/* Pedidos del cliente */}
        {cliente.pedidos && cliente.pedidos.length > 0 ? (
          <div className="bg-white rounded shadow p-3 mt-4 w-full">
            <h3 className="text-lg font-semibold mb-2">Pedidos relacionados</h3>
            <div className="space-y-2">
              {cliente.pedidos.map((pedido) => (
                <div
                  key={pedido.id}
                  className="border border-gray-200 rounded p-2 hover:border-blue-400 transition"
                >
                  <Link
                    to={`/pedidos/${pedido.id}`}
                    className="font-semibold text-gray-800 hover:text-blue-600 transition-colors"
                  >
                    Pedido #{pedido.id} â€” {pedido.tipo_operacion?.charAt(0).toUpperCase() + pedido.tipo_operacion?.slice(1)}
                  </Link>

                  <p className="text-sm text-gray-600">
                    Tipo inmueble: {pedido.tipo_inmueble || "â€”"} {pedido.subtipo_inmueble ? `(${pedido.subtipo_inmueble})` : ""}
                  </p>

                  <p className="text-sm text-gray-600">
                    Prioridad: {pedido.prioridad || "â€”"} | Presupuesto: {pedido.precio_max?.toLocaleString("es-ES") || pedido.presupuesto?.toLocaleString("es-ES") || "â€”"} â‚¬
                  </p>

                  <p className="text-xs text-gray-500">
                    Ãšltima modificaciÃ³n:{" "}
                    {pedido.fecha_ultima_modificacion
                      ? new Date(pedido.fecha_ultima_modificacion).toLocaleString("es-ES")
                      : "â€”"}
                  </p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="bg-white rounded shadow p-3 mt-4 w-full">
            <h3 className="text-lg font-semibold mb-2">Pedidos relacionados</h3>
            <p className="text-sm text-gray-600">No hay pedidos asociados a este cliente.</p>
          </div>
        )}


        {/* BotÃ³n para nuevo pedido */}
        <div className="mt-2">
          <button
            onClick={() => setShowPedidoForm(true)}
            className="text-sm text-blue-600 hover:underline"
          >
            + Nuevo Pedido
          </button>
        </div>

        {/* Modal de nuevo pedido */}
        {showPedidoForm && (
          <div className="absolute inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white rounded-lg shadow-lg p-6 w-[40rem] max-w-full relative">
              <button
                onClick={() => setShowPedidoForm(false)}
                className="absolute top-2 right-2 text-gray-600 hover:text-black text-sm"
                aria-label="Cerrar formulario"
              >
                âœ•
              </button>
              <NewPedidoForm
                clienteId={id}
                onCancel={() => setShowPedidoForm(false)}
                onCreated={async () => {
                  setShowPedidoForm(false);
                  await fetchCliente(); // ðŸ”¥ recarga datos del cliente sin recargar toda la pÃ¡gina
                }}
              />
            </div>
          </div>
        )}

      </div>
    </div>
  );
}

function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableSection({ label, value }) {
  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      <p className="text-sm">{value}</p>
      <button className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition">
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}

function ActividadCard({ actividad }) {
  return (
    <div className="bg-white shadow p-4 rounded border border-gray-200 hover:border-blue-500 space-y-2">
      <div className="flex justify-between items-center">
        <p className="text-sm font-semibold text-gray-700">{actividad.tipo || "â€”"}</p>
        <div className="flex items-center gap-1 text-sm text-gray-500">
          <span>
            {actividad.fecha_inicio
              ? new Date(actividad.fecha_inicio).toLocaleDateString()
              : "â€”"}
          </span>
          <button>
            <ChevronDown size={14} />
          </button>
        </div>
      </div>

      <p className="text-sm font-medium">{actividad.cliente?.nombre || "Cliente"}</p>

      <div className="border border-blue-600 rounded p-2 mt-[0.625rem] text-sm">
        <div className="flex justify-between">
          <div>
            <p className="text-gray-500">Agencia</p>
            <p>Falcon Group - Tarragona</p>
          </div>
          <div>
            <p className="text-gray-500">Responsable</p>
            <p>{actividad.creado_por?.username || "Usuario no disponible"}</p>
          </div>
        </div>
      </div>

      <div>
        <p className="text-sm text-gray-500 mt-2">DescripciÃ³n</p>
        <p className="text-sm">{actividad.descripcion_empleado || "Sin descripciÃ³n"}</p>
      </div>
    </div>
  );
}
