import { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function PropertyMap({ latitud, longitud, direccion }) {
  useEffect(() => {
    if (!latitud || !longitud) return;

    // --- Inicializar el mapa ---
    const map = L.map("property-map").setView([latitud, longitud], 16);

    // --- Capa de calles (OpenStreetMap) ---
    const street = L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "&copy; OpenStreetMap contributors",
    });

    // --- Capa satélite (Esri World Imagery) ---
    const satellite = L.tileLayer(
      "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
      {
        attribution:
          "Tiles © Esri &mdash; Source: Esri, i-cubed, USDA, USGS, AEX, GeoEye, Getmapping, Aerogrid, IGN, IGP, UPR-EGP, and the GIS User Community",
      }
    );

    // Añadir por defecto vista de calles
    street.addTo(map);

    // --- Control para alternar capas ---
    L.control.layers({ "Vista de calles": street, "Vista satélite": satellite }).addTo(map);

    // --- Marcador ---
    L.marker([latitud, longitud])
      .addTo(map)
      .bindPopup(`<b>${direccion || "Ubicación del inmueble"}</b>`)
      .openPopup();

    return () => {
      map.remove();
    };
  }, [latitud, longitud, direccion]);

  return (
    <div
      id="property-map"
      className="w-full h-56 rounded shadow border"
      style={{ minHeight: "220px" }}
    />
  );
}
