// Al inicio, importa React, axios, etc.
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Pencil } from "lucide-react";

export default function ClientDetail() {
  const { id } = useParams();
  const [cliente, setCliente] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/clientes/${id}/`)
      .then(res => setCliente(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!cliente) return <div>Cargando datos del cliente...</div>;

  return (
    <div className="flex gap-4 w-full">
      {/* Sección izquierda: 2/3 */}
      <div className="w-2/3 space-y-4">
        {/* Rectángulo superior */}
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock label="Último contacto" value={cliente.fecha_ultimo_contacto} />
          <InfoBlock label="Ocupado por" value={cliente.ocupado_por} />
          <InfoBlock label="Informadores" value={cliente.informadores?.join(", ") || "—"} />
          <InfoBlock label="Actividades" value={cliente.actividades?.length || 0} />
          <InfoBlock label="Última venta/alquiler" value={cliente.fecha_ultima_venta_alquiler} />
          <InfoBlock label="Días desde contacto" value={`${cliente.dias_ultimo_contacto} días`} />
        </div>

        {/* Zona inferior: datos generales del cliente */}
        <div className="flex gap-4">
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Dirección" value={cliente.direccion} />
            <EditableSection label="Tipo Documento" value={cliente.tipo_documento} />
            <EditableSection label="Nº Identificación" value={cliente.num_identificacion} />
          </div>
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Trato" value={cliente.trato} />
            <EditableSection label="Sexo" value={cliente.sexo} />
            <EditableSection label="Info adicional" value={cliente.info_adicional || "—"} />
          </div>
        </div>

        {/* Nueva sección: Inmuebles */}
        <div className="bg-white border rounded shadow p-4 space-y-4">
          <h3 className="text-lg font-semibold">Inmuebles relacionados</h3>
          {(cliente.inmuebles_propietario || []).concat(cliente.inmuebles_inquilino || []).map((inmueble, idx) => (
            <div key={idx} className="border-t pt-4">
              <div className="grid grid-cols-3 gap-2 text-sm">
                <InfoBlock label="Planta" value={inmueble.planta} />
                <InfoBlock label="Puerta" value={inmueble.puerta} />
                <InfoBlock label="Interior" value={inmueble.interior} />
                <InfoBlock label="Ref. Catastral" value={inmueble.ref_catastral} />
                <InfoBlock label="Cert. Energético" value={inmueble.estado_certificado} />
                <InfoBlock label="Emisiones CO2" value={inmueble.emisiones_CO2} />
                <InfoBlock label="Consumo Energía" value={inmueble.consumo_energia} />
                <InfoBlock label="Habitaciones" value={inmueble.habitaciones} />
                <InfoBlock label="Baños" value={inmueble.banos} />
                <InfoBlock label="Estancias" value={inmueble.estancias} />
                <InfoBlock label="Balcón" value={inmueble.balcon ? "Sí" : "No"} />
                <InfoBlock label="Jardín" value={inmueble.jardin ? "Sí" : "No"} />
                <InfoBlock label="Zona Asignada" value={inmueble.zona_asignada || "—"} />
                <InfoBlock label="Día zona" value={inmueble.dia_zona || "—"} />
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Sección derecha: actividades recientes */}
      <div className="w-1/3 space-y-4">
        <h3 className="text-lg font-semibold">Últimas Actividades</h3>
        {[1, 2, 3].map((_, idx) => (
          <div key={idx} className="bg-white shadow p-4 rounded border hover:border-blue-500">
            <p className="text-sm text-gray-500">Actividad #{idx + 1}</p>
            <p>Detalle de la actividad más reciente...</p>
          </div>
        ))}
      </div>
    </div>
  );
}

// Subcomponentes
function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-xs text-gray-500">{label}</p>
      <p className="text-sm font-medium">{value || "—"}</p>
    </div>
  );
}

function EditableSection({ label, value }) {
  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      <p className="text-sm">{value}</p>
      <button className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition">
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}
