import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";

export default function NewPedidoForm({ clienteId, onCancel, onCreated }) {
  const navigate = useNavigate();
  const [clientes, setClientes] = useState([]);
  const [formData, setFormData] = useState({
    cliente: clienteId || "", // ✅ si viene desde ClientDetail, se preselecciona
    tipo_pedido: "",
    descripcion: "",
    prioridad: "Media",
    estado: "Pendiente",
    presupuesto: "",
    fecha_entrega: "",
  });

  const [enviando, setEnviando] = useState(false);
  const [error, setError] = useState(null);

  // Cargar lista de clientes si no se ha pasado clienteId
  useEffect(() => {
    if (!clienteId) fetchClientes();
  }, [clienteId]);

  const fetchClientes = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const res = await api.get("/clientes/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setClientes(res.data);
    } catch (err) {
      console.error("Error cargando clientes:", err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleGuardar = async () => {
    setError(null);
    if (!formData.cliente) return setError("Debes seleccionar un cliente.");
    if (!formData.tipo_pedido) return setError("Debes indicar el tipo de pedido.");

    setEnviando(true);
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) throw new Error("No estás autenticado.");

      await api.post("/pedidos/", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (onCreated) onCreated(); // ✅ refresca el cliente en ClientDetail
      else navigate("/solicitudes");
    } catch (err) {
      console.error("Error creando pedido:", err);
      setError(
        err.response?.data
          ? JSON.stringify(err.response.data, null, 2)
          : err.message
      );
    } finally {
      setEnviando(false);
    }
  };

  return (
    <div className="bg-white w-[90%] md:w-2/3 p-6 rounded-lg shadow-xl space-y-4 relative">
      <h2 className="text-lg font-semibold">Nuevo Pedido</h2>

      {/* Cliente */}
      <div>
        <label className="block text-sm font-medium mb-1">Cliente</label>
        {clienteId ? (
          <input
            type="text"
            value={`Cliente #${clienteId}`}
            disabled
            className="w-full border rounded px-3 py-2 bg-gray-100"
          />
        ) : (
          <select
            name="cliente"
            value={formData.cliente}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="">Seleccionar cliente...</option>
            {clientes.map((c) => (
              <option key={c.id} value={c.id}>
                {c.nombre} {c.apellido1}
              </option>
            ))}
          </select>
        )}
      </div>

      {/* Tipo de pedido */}
      <div>
        <label className="block text-sm font-medium mb-1">Tipo de Pedido</label>
        <input
          name="tipo_pedido"
          value={formData.tipo_pedido}
          onChange={handleChange}
          placeholder="Ej: Venta, Alquiler, Búsqueda..."
          className="w-full border rounded px-3 py-2"
        />
      </div>

      {/* Descripción */}
      <div>
        <label className="block text-sm font-medium mb-1">Descripción</label>
        <textarea
          name="descripcion"
          value={formData.descripcion}
          onChange={handleChange}
          placeholder="Detalles del pedido..."
          className="w-full border rounded px-3 py-2"
        />
      </div>

      {/* Prioridad y Estado */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Prioridad</label>
          <select
            name="prioridad"
            value={formData.prioridad}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="Alta">Alta</option>
            <option value="Media">Media</option>
            <option value="Baja">Baja</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">Estado</label>
          <select
            name="estado"
            value={formData.estado}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="Pendiente">Pendiente</option>
            <option value="En curso">En curso</option>
            <option value="Completado">Completado</option>
            <option value="Cancelado">Cancelado</option>
          </select>
        </div>
      </div>

      {/* Presupuesto y Fecha */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Presupuesto (€)</label>
          <input
            type="number"
            name="presupuesto"
            value={formData.presupuesto}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Fecha de Entrega</label>
          <input
            type="date"
            name="fecha_entrega"
            value={formData.fecha_entrega}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
        </div>
      </div>

      {/* Errores */}
      {error && (
        <pre className="text-red-600 whitespace-pre-wrap text-sm bg-red-50 p-2 rounded">
          {error}
        </pre>
      )}

      {/* Botones */}
      <div className="flex justify-end gap-4">
        <button
          onClick={onCancel || (() => navigate("/solicitudes"))}
          className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
        >
          Cancelar
        </button>
        <button
          onClick={handleGuardar}
          disabled={enviando}
          className={`px-4 py-2 rounded text-white ${
            enviando ? "bg-blue-400" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {enviando ? "Guardando..." : "Guardar"}
        </button>
      </div>
    </div>
  );
}
