import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Pencil } from "lucide-react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";
import SearchCliente from "./SearchCliente";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function PropertyDetail() {
  const { id } = useParams();
  const [inmueble, setInmueble] = useState(null);
  const [originalInmueble, setOriginalInmueble] = useState(null);
  const [actividades, setActividades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [showAllActivities, setShowAllActivities] = useState(false);
  const [editedFields, setEditedFields] = useState({});
  const [saving, setSaving] = useState(false);
  const [searchActivity, setSearchActivity] = useState("");

  const fetchInmueble = async () => {
    try {
      const res = await api.get(`/inmuebles/${id}/`);
      setInmueble(res.data);
      setOriginalInmueble(res.data);
    } catch (err) {
      console.error(err);
      setError("Error al cargar el inmueble.");
    }
  };

  const fetchActividades = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const res = await api.get(`/actividades/inmueble/${id}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setActividades(res.data);
    } catch (err) {
      console.error("Error cargando actividades:", err);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      setError(null);
      setLoading(true);
      try {
        await fetchInmueble();
        await fetchActividades();
      } catch {
        setError("Error cargando datos");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  if (loading) return <div>Cargando...</div>;
  if (error)
    return <div className="text-red-600 bg-red-100 p-4 rounded">{error}</div>;
  if (!inmueble) return <div>Datos no disponibles.</div>;

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("es-ES") : "—";

  const handleFieldChange = (field, value) => {
    setInmueble((prev) => ({ ...prev, [field]: value }));
    setEditedFields((prev) => ({ ...prev, [field]: value }));
  };

  const handleGuardar = async () => {
    setSaving(true);
    try {
      const token = localStorage.getItem("accessToken");
      await api.patch(`/inmuebles/${id}/`, editedFields, {
        headers: { Authorization: `Bearer ${token}` },
      });
      await fetchInmueble();
      setEditedFields({});
    } catch (err) {
      console.error("Error guardando:", err);
    } finally {
      setSaving(false);
    }
  };

  const handleCancelar = () => {
    setInmueble(originalInmueble);
    setEditedFields({});
  };

  const filteredActivities = actividades.filter((a) => {
    const q = searchActivity.toLowerCase();
    return (
      a.descripcion_empleado?.toLowerCase().includes(q) ||
      a.estado?.toLowerCase().includes(q) ||
      a.usuario_responsable?.username?.toLowerCase().includes(q) ||
      new Date(a.fecha_inicio)
        .toLocaleDateString("es-ES")
        .includes(q)
    );
  });

  return (
    <div className="flex gap-4 w-full relative">
      {showForm && (
        <Modal onClose={() => setShowForm(false)}>
          <NewActivityForm
            inmuebleId={id}
            onCancel={() => setShowForm(false)}
            onCreated={async () => {
              setShowForm(false);
              await fetchActividades();
            }}
          />
        </Modal>
      )}

      {showAllActivities && (
        <Modal onClose={() => setShowAllActivities(false)}>
          <div className="space-y-2">
            <h3 className="text-lg font-semibold mb-2">Todas las actividades</h3>
            <input
              type="text"
              placeholder="Buscar por fecha, responsable..."
              className="w-full border rounded p-2"
              value={searchActivity}
              onChange={(e) => setSearchActivity(e.target.value)}
            />
            <div className="max-h-80 overflow-auto mt-2 space-y-2">
              {filteredActivities.map((actividad) => (
                <ActividadCard key={actividad.id} actividad={actividad} />
              ))}
            </div>
          </div>
        </Modal>
      )}

      {/* IZQUIERDA */}
      <div className="w-2/3 space-y-4">
        {/* Encabezado principal */}
        <div className="bg-white rounded shadow p-4">
          <h2 className="text-xl font-bold">
            {inmueble.edificio?.calle} {inmueble.edificio?.numero_calle} - Planta{" "}
            {inmueble.planta}, Puerta {inmueble.puerta}
            {inmueble.interior ? `, Interior ${inmueble.interior}` : ""}
          </h2>
        </div>

        {/* 3x2 */}
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock
            label="Último contacto"
            value={formatDate(inmueble.fecha_ultimo_contacto)}
          />
          <InfoBlock
            label="Ocupado por"
            value={inmueble.ocupado_por || "—"}
          />
          <InfoBlock label="Actividades" value={actividades.length} />
          <InfoBlock
            label="Última venta/alquiler"
            value={formatDate(inmueble.fecha_ultima_venta_alquiler)}
          />
          <InfoBlock
            label="Última modificación"
            value={formatDate(inmueble.fecha_ultima_modificacion)}
          />
          <InfoBlock
            label="Zona asignada"
            value={inmueble.zona_asignada || "—"}
          />
        </div>

        {/* Botonera Estado CRM */}
        <div className="flex gap-2 bg-white rounded shadow p-2 justify-center">
          {["Por defecto", "Noticia", "Encargo"].map((estado) => (
            <button
              key={estado}
              onClick={() => handleFieldChange("estado_CRM", estado)}
              className={`px-4 py-1 text-sm rounded border ${
                inmueble.estado_CRM === estado
                  ? "bg-blue-600 text-white"
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              }`}
            >
              {estado === "Por defecto" ? "Inmueble" : estado}
            </button>
          ))}
        </div>

        {/* Información principal */}
        <h3 className="text-lg font-semibold">Información principal</h3>
        <div className="bg-white border rounded shadow divide-y">
          <EditableSection
            label="Planta"
            value={inmueble.planta}
            onChange={(v) => handleFieldChange("planta", v)}
          />
          <EditableSection
            label="Puerta"
            value={inmueble.puerta}
            onChange={(v) => handleFieldChange("puerta", v)}
          />
          <EditableSection
            label="Interior"
            value={inmueble.interior || "—"}
            onChange={(v) => handleFieldChange("interior", v)}
          />
          <EditableSection
            label="Referencia Catastral"
            value={inmueble.ref_catastral || "—"}
            onChange={(v) => handleFieldChange("ref_catastral", v)}
          />
          <EditableSection
            label="Propietario"
            value={
              inmueble.propietario
                ? `${inmueble.propietario.nombre} ${
                    inmueble.propietario.apellido1 || ""
                  } ${inmueble.propietario.apellido2 || ""}`.trim()
                : "Sin propietario"
            }
          >
            <SearchCliente
              selected={inmueble.propietario}
              onSelect={(cliente) =>
                handleFieldChange("propietario_id", cliente.id)
              }
            />
          </EditableSection>
        </div>

        {/* Características del inmueble */}
        <h3 className="text-lg font-semibold mt-4">
          Características del inmueble
        </h3>
        <div className="flex gap-4">
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection
              label="Habitaciones"
              value={inmueble.habitaciones}
              onChange={(v) => handleFieldChange("habitaciones", v)}
            />
            <EditableSection
              label="Baños"
              value={inmueble.banos}
              onChange={(v) => handleFieldChange("banos", v)}
            />
            <EditableSection
              label="Estancias"
              value={inmueble.estancias}
              onChange={(v) => handleFieldChange("estancias", v)}
            />
            <EditableSection
              label="Balcón"
              value={inmueble.balcon ? "Sí" : "No"}
              onChange={(v) => handleFieldChange("balcon", v === "Sí")}
            />
            <EditableSection
              label="Jardín"
              value={inmueble.jardin ? "Sí" : "No"}
              onChange={(v) => handleFieldChange("jardin", v === "Sí")}
            />
          </div>
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection
              label="Certificado Energético"
              value={inmueble.estado_certificado || "—"}
              onChange={(v) => handleFieldChange("estado_certificado", v)}
            />
            <EditableSection
              label="Consumo Energía"
              value={inmueble.consumo_energia ?? "—"}
              onChange={(v) => handleFieldChange("consumo_energia", v)}
            />
            <EditableSection
              label="Emisiones CO2"
              value={inmueble.emisiones_CO2 ?? "—"}
              onChange={(v) => handleFieldChange("emisiones_CO2", v)}
            />
            <EditableSection
              label="Nota Simple"
              value={inmueble.nota_simple ? "Sí" : "No"}
              onChange={(v) => handleFieldChange("nota_simple", v === "Sí")}
            />
            <EditableSection
              label="Día de Zona"
              value={formatDate(inmueble.dia_zona)}
              onChange={(v) => handleFieldChange("dia_zona", v)}
            />
          </div>
        </div>

        {/* Otros datos del inmueble */}
        <h3 className="text-lg font-semibold mt-4">Otros datos del inmueble</h3>
        <div className="bg-white border rounded shadow divide-y">
          <EditableSection
            label="Motivación"
            value={inmueble.motivacion}
            onChange={(v) => handleFieldChange("motivacion", v)}
          />
          <EditableSection
            label="Precio pedido cliente"
            value={inmueble.precio_pedido_cliente ?? "—"}
            onChange={(v) => handleFieldChange("precio_pedido_cliente", v)}
          />
          <EditableSection
            label="Valoración"
            value={inmueble.valoracion ?? "—"}
            onChange={(v) => handleFieldChange("valoracion", v)}
          />
          <EditableSection
            label="Fecha valoración"
            value={formatDate(inmueble.fecha_valoracion)}
            onChange={(v) => handleFieldChange("fecha_valoracion", v)}
          />
          <EditableSection
            label="Necesidad de venta"
            value={inmueble.necesidad_venta}
            onChange={(v) => handleFieldChange("necesidad_venta", v)}
          />
          <EditableSection
            label="Prioridad noticia"
            value={inmueble.prioridad_noticia}
            onChange={(v) => handleFieldChange("prioridad_noticia", v)}
          />
          <EditableSection
            label="Tipo procedencia"
            value={inmueble.tipo_procedencia}
            onChange={(v) => handleFieldChange("tipo_procedencia", v)}
          />
          <EditableSection
            label="Nota inmueble"
            value={inmueble.nota_inmueble || "—"}
            onChange={(v) => handleFieldChange("nota_inmueble", v)}
          />
          <EditableSection
            label="Nota noticia"
            value={inmueble.nota_noticia || "—"}
            onChange={(v) => handleFieldChange("nota_noticia", v)}
          />
          <EditableSection
            label="Comisión"
            value={inmueble.comision ?? "—"}
            onChange={(v) => handleFieldChange("comision", v)}
          />
        </div>

        {/* Footer dinámico */}
        {Object.keys(editedFields).length > 0 && (
          <div className="flex justify-end gap-4 mt-4 bg-gray-50 p-3 rounded shadow">
            <button
              onClick={handleCancelar}
              className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
            >
              Cancelar
            </button>
            <button
              onClick={handleGuardar}
              disabled={saving}
              className={`px-4 py-2 rounded text-white ${
                saving ? "bg-blue-400" : "bg-blue-600 hover:bg-blue-700"
              }`}
            >
              {saving ? "Guardando..." : "Guardar"}
            </button>
          </div>
        )}
      </div>

      {/* DERECHA */}
      <div className="w-1/3 space-y-4">
        {/* Actividades siempre arriba */}
        <div>
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-lg font-semibold">Actividades recientes</h3>
            <button
              onClick={() => setShowForm(true)}
              className="text-sm text-blue-600 hover:underline"
            >
              + Nueva
            </button>
          </div>
          {actividades.length === 0 ? (
            <p>No hay actividades registradas.</p>
          ) : (
            actividades.slice(0, 2).map((actividad) => (
              <ActividadCard key={actividad.id} actividad={actividad} />
            ))
          )}
          {actividades.length > 2 && (
            <button
              onClick={() => setShowAllActivities(true)}
              className="text-sm text-blue-600 hover:underline mt-1"
            >
              Ver más
            </button>
          )}
        </div>

        {/* Datos básicos del propietario */}
        {inmueble.propietario && (
          <div className="bg-white rounded shadow p-3 space-y-1">
            <h3 className="text-lg font-semibold mb-2">Datos del propietario</h3>
            {[
              { key: "nombre", label: "Nombre" },
              { key: "apellido1", label: "Apellido 1" },
              { key: "apellido2", label: "Apellido 2" },
              { key: "telefono", label: "Teléfono" },
              { key: "telefono_movil", label: "Teléfono móvil" },
              { key: "email", label: "Email" },
              { key: "direccion", label: "Dirección" },
            ].map((field) => (
              <EditableSection
                key={field.key}
                label={field.label}
                value={inmueble.propietario[field.key] || "—"}
                onChange={(v) =>
                  handleFieldChange(`propietario.${field.key}`, v)
                }
              />
            ))}
          </div>
        )}

        {/* Mapa */}
        <div className="bg-white rounded shadow p-2">
          <h3 className="text-lg font-semibold mb-2">Ubicación</h3>
          <MapContainer
            center={[
              inmueble.edificio?.latitud || 41.12,
              inmueble.edificio?.longitud || 1.24,
            ]}
            zoom={16}
            className="h-64 w-full rounded"
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; OpenStreetMap contributors"
            />
            <Marker
              position={[
                inmueble.edificio?.latitud || 41.12,
                inmueble.edificio?.longitud || 1.24,
              ]}
            >
              <Popup>
                {inmueble.edificio?.calle} {inmueble.edificio?.numero_calle}
              </Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>
    </div>
  );
}

function Modal({ children, onClose }) {
  return (
    <div className="absolute inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white rounded-lg shadow-lg p-4 w-[40rem] max-w-full relative">
        <button
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-600 hover:text-black text-sm"
        >
          ✕
        </button>
        {children}
      </div>
    </div>
  );
}

function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableSection({ label, value, onChange, children }) {
  const [editing, setEditing] = useState(false);
  const [tempValue, setTempValue] = useState(value);
  useEffect(() => setTempValue(value), [value]);

  return (
    <div className="p-2 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      {editing ? (
        children ? (
          children
        ) : (
          <input
            type="text"
            className="w-full border rounded px-2 py-1 text-sm"
            value={tempValue === "—" ? "" : tempValue}
            onChange={(e) => setTempValue(e.target.value)}
            onBlur={() => {
              onChange(tempValue);
              setEditing(false);
            }}
            autoFocus
          />
        )
      ) : (
        <p className="text-sm">{value}</p>
      )}
      <button
        onClick={() => setEditing(true)}
        className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition"
      >
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}

function ActividadCard({ actividad }) {
  return (
    <div className="bg-white shadow p-4 rounded border border-gray-200 hover:border-blue-500 space-y-2">
      <div className="flex justify-between items-center">
        <p className="text-sm font-semibold text-gray-700">
          {actividad.tipo || "—"}
        </p>
        <div className="text-sm text-gray-500">
          {actividad.fecha_inicio
            ? new Date(actividad.fecha_inicio).toLocaleDateString("es-ES")
            : "—"}
        </div>
      </div>
      <p className="text-sm font-medium">
        {actividad.inmueble?.ref_catastral || "Inmueble"}
      </p>
      <div className="border border-blue-600 rounded p-2 text-sm">
        <div className="flex justify-between">
          <div>
            <p className="text-gray-500">Agencia</p>
            <p>Falcon Group - Tarragona</p>
          </div>
          <div>
            <p className="text-gray-500">Responsable</p>
            <p>
              {actividad.usuario_responsable
                ? `${actividad.usuario_responsable.first_name || ""} ${
                    actividad.usuario_responsable.last_name || ""
                  }`.trim() || actividad.usuario_responsable.username
                : "Usuario no disponible"}
            </p>
          </div>
        </div>
      </div>
      <div>
        <p className="text-sm text-gray-500 mt-2">Descripción</p>
        <p className="text-sm">
          {actividad.descripcion_empleado || "Sin descripción"}
        </p>
      </div>
    </div>
  );
}
