// src/components/ClientDetail.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Pencil } from "lucide-react";

export default function ClientDetail() {
  const { id } = useParams();
  const [cliente, setCliente] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/clientes/${id}/`)
      .then(res => setCliente(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!cliente) return <div>Cargando datos del cliente...</div>;

  return (
    <div className="flex gap-4 w-full">
      {/* Sección izquierda: 2/3 */}
      <div className="w-2/3 space-y-4">
        {/* Rectángulo superior */}
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock label="Último contacto" value="15/06/2025" />
          <InfoBlock label="Ocupado por" value="Juan Pérez" />
          <InfoBlock label="Informadores" value="María López" />
          <InfoBlock label="Actividades" value="12" />
          <InfoBlock label="Última venta/alquiler" value="02/05/2025" />
          <InfoBlock label="Días desde contacto" value="6 días" />
        </div>

        {/* Zona inferior: dos contenedores 50% */}
        <div className="flex gap-4">
          {/* Contenedor izquierdo */}
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Dirección" value={cliente.direccion} />
            <EditableSection label="Tipo Documento" value={cliente.tipo_documento} />
            <EditableSection label="Nº Identificación" value={cliente.num_identificacion} />
          </div>

          {/* Contenedor derecho */}
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Trato" value={cliente.trato} />
            <EditableSection label="Sexo" value={cliente.sexo} />
            <EditableSection label="Info adicional" value={cliente.info_adicional || "—"} />
          </div>
        </div>
      </div>

      {/* Sección derecha: 1/3 */}
      <div className="w-1/3 space-y-4">
        <h3 className="text-lg font-semibold">Últimas Actividades</h3>
        {[1, 2, 3].map((_, idx) => (
          <div
            key={idx}
            className="bg-white shadow p-4 rounded border hover:border-blue-500"
          >
            <p className="text-sm text-gray-500">Actividad #{idx + 1}</p>
            <p>Detalle de la actividad más reciente...</p>
          </div>
        ))}
      </div>
    </div>
  );
}

// Subcomponentes reutilizables
function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableSection({ label, value }) {
  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      <p className="text-sm">{value}</p>
      <button className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition">
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}
