// src/components/UserPanel.jsx
import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import Login from "./Login";
import Register from "./Register";
import { authFetch } from "./api/auth";
import DayView from "./DayView";

export default function UserPanel() {
  const { logout, isAuthenticated, user } = useContext(AuthContext);

  const [actividadesPlan, setActividadesPlan] = useState([]);
  const [actividadesRealizadas, setActividadesRealizadas] = useState([]);
  const [objetivos, setObjetivos] = useState([]);

  // ðŸ”¥ Wrapper interno para limitar horario sin tocar DayView original
  function DayViewLimited(props) {
    const calendarUtils = require("./calendarUtils");

    const originalStart = calendarUtils.startHour;
    const originalEnd = calendarUtils.endHour;

    // Fuerza horario 08:00â€“22:00
    calendarUtils.startHour = 8;
    calendarUtils.endHour = 22;

    const result = <DayView {...props} />;

    // Restauramos valores despuÃ©s de renderizar
    calendarUtils.startHour = originalStart;
    calendarUtils.endHour = originalEnd;

    return result;
  }

  useEffect(() => {
    if (!isAuthenticated) return;

    const load = async () => {
      try {
        const res = await authFetch("/api/actividades/");
        const data = await res.json();

        const hoy = new Date().toDateString();

        const plan = data.filter(a => {
          const inicio = new Date(a.fecha_inicio);
          return (
            inicio.toDateString() === hoy &&
            a.estado?.toLowerCase() === "planificacion"
          );
        });

        const realizadas = data.filter(a => {
          const inicio = new Date(a.fecha_inicio);
          return (
            inicio.toDateString() === hoy &&
            a.estado?.toLowerCase() === "realizada"
          );
        });

        setActividadesPlan(plan);
        setActividadesRealizadas(realizadas);

        setObjetivos([
          { nombre: "Llamadas", actual: 12, meta: 30 },
          { nombre: "Visitas", actual: 5, meta: 12 },
          { nombre: "Captaciones", actual: 1, meta: 5 },
        ]);
      } catch (e) {
        console.error("Error cargando datos panel:", e);
      }
    };

    load();
  }, [isAuthenticated]);

  if (!isAuthenticated) {
    return (
      <Login />
    );
  }

  return (
    <div className="flex w-full min-h-[85dvh] max-h-[85dvh] overflow-hidden">
      {/* Columna 1 */}
      <div className="w-1/3 flex flex-col border-r p-2">
        {/* Actividades en planificaciÃ³n */}
        <div className="h-1/2 overflow-auto p-2 border-b">
          <h3 className="font-semibold text-lg mb-2">Actividades Planificadas Hoy</h3>
          {actividadesPlan.length === 0 ? (
            <p className="text-sm text-gray-500">No hay actividades planificadas.</p>
          ) : (
            <ul className="space-y-2">
              {actividadesPlan.map(a => (
                <li key={a.id} className="p-2 bg-yellow-100 rounded shadow text-sm">
                  {a.descripcion_publica}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Objetivos del mes */}
        <div className="h-1/2 overflow-y-auto p-2">
          <h3 className="font-semibold text-lg mb-3">Objetivos del Mes</h3>

          <div className="grid grid-cols-2 gap-x-6 gap-y-10 pr-1">
            {[
              { nombre: "Llamadas", actual: 12, meta: 30 },
              { nombre: "Visitas", actual: 5, meta: 12 },
              { nombre: "Captaciones", actual: 1, meta: 5 },
              { nombre: "Reuniones", actual: 3, meta: 8 },
              { nombre: "Cierres", actual: 1, meta: 3 },
              { nombre: "Zonas", actual: 2, meta: 6 },
            ].map((obj, idx) => {
              const progreso = Math.min(obj.actual / obj.meta, 1);

              // medidas del arco 3/4
              const size = 120;      
              const stroke = 10;     
              const radius = 45;     
              const circTotal = 2 * Math.PI * radius;
              const arc = (3 / 4) * circTotal;     
              const offset = arc * (1 - progreso); 

              return (
                <div
                  key={idx}
                  className="flex flex-col items-center justify-center text-center"
                  style={{ minHeight: "165px" }}
                >
                  <div className="relative" style={{ width: size, height: size * 0.8 }}>
                    <svg width={size} height={size} viewBox="0 0 120 120">
                      {/* Arco base 3/4 */}
                      <path
                        d="M 30 90 A 45 45 0 1 1 90 90"
                        stroke="#e5e7eb"
                        strokeWidth={stroke}
                        fill="none"
                        strokeLinecap="round"
                        strokeDasharray={arc}
                        strokeDashoffset={0}
                      />

                      {/* Progreso */}
                      <path
                        d="M 30 90 A 45 45 0 1 1 90 90"
                        stroke="#10b981"
                        strokeWidth={stroke}
                        fill="none"
                        strokeLinecap="round"
                        strokeDasharray={arc}
                        strokeDashoffset={offset}
                      />
                    </svg>

                    {/* Porcentaje */}
                    <div className="absolute inset-0 flex items-center justify-center text-sm font-semibold">
                      {Math.round(progreso * 100)}%
                    </div>
                  </div>

                  {/* Nombre y valores */}
                  <div className="text-sm font-medium mt-2">{obj.nombre}</div>
                  <div className="text-xs text-gray-500 mt-1">
                    {obj.actual} / {obj.meta}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* Columna 2 */}
      <div className="w-1/3 p-2 border-r overflow-auto">
        <h3 className="font-semibold text-lg mb-2">Actividades Realizadas Hoy</h3>
        {actividadesRealizadas.length === 0 ? (
          <p className="text-sm text-gray-500">AÃºn no has realizado actividades hoy.</p>
        ) : (
          <ul className="space-y-2">
            {actividadesRealizadas.map(a => (
              <li key={a.id} className="p-2 bg-green-100 rounded shadow text-sm">
                {a.descripcion_publica}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Columna 3 */}
      <div className="w-1/3 p-2 flex flex-col">
        <h3 className="font-semibold text-lg mb-2">Calendario del DÃ­a</h3>

        {/* Contenedor con scroll vertical */}
        <div
          className="flex-1 border rounded overflow-y-auto overflow-x-hidden"
          style={{
            maxHeight: "92%",
            scrollBehavior: "smooth",
          }}
        >
          <div
            style={{
              minHeight: "1200px", // suficiente para cubrir 8â€“22h (14 horas * 4 bloques/hora * 20px = 1120)
              paddingBottom: "20px",
            }}
          >
            <DayView usuariosSeleccionados={[user?.id]} />
          </div>
        </div>
      </div>
    </div>
  );
}
