import { useState } from "react";
import axios from "axios";

export default function ClientForm({ onSuccess }) {
  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    telefono: "",
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.post("http://localhost:8000/api/clientes/", formData)
      .then(() => {
        onSuccess();
        setFormData({ nombre: "", email: "", telefono: "" });
      })
      .catch(err => console.error(err));
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 shadow rounded space-y-2">
      <h2 className="text-xl font-bold">Añadir Cliente</h2>
      <input name="nombre" value={formData.nombre} onChange={handleChange} placeholder="Nombre" className="w-full border p-2 rounded" />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" className="w-full border p-2 rounded" />
      <input name="telefono" value={formData.telefono} onChange={handleChange} placeholder="Teléfono" className="w-full border p-2 rounded" />
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Guardar</button>
    </form>
  );
}
