import { useEffect, useState } from "react";
import { CalendarIcon } from "lucide-react";
import WeekView from "./WeekView";
import DayView from "./DayView";
import MonthView from "./MonthView";
import api from "../api";

const VISTAS = {
  DIA: "DÍA",
  SEMANA: "SEMANA",
  MES: "MES",
};

export default function Calendar() {
  const [vista, setVista] = useState(VISTAS.SEMANA);
  const [usuarios, setUsuarios] = useState([]);
  const [usuariosSeleccionados, setUsuariosSeleccionados] = useState([]);

  useEffect(() => {
    const fetchUsuarios = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await api.get("/usuarios/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsuarios(res.data);
        setUsuariosSeleccionados(res.data.map((u) => u.id)); // todos seleccionados por defecto
      } catch (error) {
        console.error("Error al obtener usuarios:", error);
      }
    };
    fetchUsuarios();
  }, []);

  const toggleUsuario = (id) => {
    setUsuariosSeleccionados((prev) =>
      prev.includes(id)
        ? prev.filter((uid) => uid !== id)
        : [...prev, id]
    );
  };

  const handleSelectChange = (e) => {
    const selected = Array.from(e.target.selectedOptions).map(opt => parseInt(opt.value));
    setUsuariosSeleccionados(selected);
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Barra superior con vista y filtros */}
      <div className="flex items-center justify-between p-4 border-b bg-gray-50">
        <div className="flex gap-4 items-center">
          <CalendarIcon className="w-5 h-5" />
          <select
            value={vista}
            onChange={(e) => setVista(e.target.value)}
            className="border rounded px-2 py-1"
          >
            <option value={VISTAS.DIA}>Vista diaria</option>
            <option value={VISTAS.SEMANA}>Vista semanal</option>
            <option value={VISTAS.MES}>Vista mensual</option>
          </select>
        </div>

        {/* Select múltiple de usuarios */}
        <div className="relative">
          <select
            multiple
            size={1}
            onChange={handleSelectChange}
            value={usuariosSeleccionados.map(String)}
            className="border rounded px-2 py-1 min-w-[200px] max-w-xs overflow-auto text-xs"
          >
            {usuarios.map((u) => {
              const label = u.first_name || u.last_name
                ? `${u.first_name || ""} ${u.last_name || ""}`.trim()
                : u.username;
              return (
                <option key={u.id} value={u.id}>
                  {label}
                </option>
              );
            })}
          </select>
        </div>
      </div>

      {/* Contenido según vista */}
      <div className="flex-1 overflow-auto">
        {vista === VISTAS.DIA && (
          <DayView usuariosSeleccionados={usuariosSeleccionados} />
        )}
        {vista === VISTAS.SEMANA && (
          <WeekView usuariosSeleccionados={usuariosSeleccionados} />
        )}
        {vista === VISTAS.MES && (
          <MonthView usuariosSeleccionados={usuariosSeleccionados} />
        )}
      </div>
    </div>
  );
}
