// src/context/AuthContext.jsx
import React, { createContext, useState, useEffect } from "react";

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);  // aquí guardaremos info de usuario (opcional)
  const [token, setToken] = useState(null);

  // Comprobar token en localStorage al cargar la app
  useEffect(() => {
    const savedToken = localStorage.getItem("token");
    if (savedToken) {
      setToken(savedToken);
      // Aquí podrías también decodificar el token para extraer info de usuario
      // o hacer una llamada a la API para validar sesión
      setUser({}); // placeholder usuario logueado
    }
  }, []);

  // Función para hacer login y guardar token
  const login = (data) => {
    setToken(data.access || data.token); // asumiendo que backend devuelve token en data.access o data.token
    localStorage.setItem("token", data.access || data.token);
    setUser({}); // set user info si tienes
  };

  // Función para logout: borrar token y usuario
  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem("token");
  };

  const isAuthenticated = !!token;

  return (
    <AuthContext.Provider
      value={{ user, token, login, logout, isAuthenticated }}
    >
      {children}
    </AuthContext.Provider>
  );
}
