// src/components/ClientDetail.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function ClientDetail() {
  const { id } = useParams();
  const [cliente, setCliente] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/clientes/${id}/`)
      .then(res => setCliente(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!cliente) return <div>Cargando datos del cliente...</div>;

  return (
    <div className="bg-white p-6 shadow rounded w-full">
      <h2 className="text-2xl font-bold mb-4">Ficha del Cliente</h2>
      <ul className="space-y-2">
        <li><strong>Nombre:</strong> {cliente.nombre}</li>
        <li><strong>Primer Apellido:</strong> {cliente.apellido1}</li>
        <li><strong>Segundo Apellido:</strong> {cliente.apellido2 || "—"}</li>
        <li><strong>Trato:</strong> {cliente.trato}</li>
        <li><strong>Sexo:</strong> {cliente.sexo}</li>
        <li><strong>Tipo Documento:</strong> {cliente.tipo_documento}</li>
        <li><strong>Nº Identificación:</strong> {cliente.num_identificacion}</li>
        <li><strong>Dirección:</strong> {cliente.direccion}</li>
        <li><strong>Información Adicional:</strong> {cliente.info_adicional || "—"}</li>
      </ul>
    </div>
  );
}
