import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Pencil } from "lucide-react";

export default function PropertyDetail() {
  const { id } = useParams();
  const [inmueble, setInmueble] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/inmuebles/${id}/`)
      .then((res) => setInmueble(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!inmueble) return <div>Cargando datos del inmueble...</div>;

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("es-ES") : "—";

  return (
    <div className="flex gap-4 w-full">
      {/* Sección izquierda: 2/3 */}
      <div className="w-2/3 space-y-4">
        {/* Rectángulo superior */}
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock
            label="Último contacto"
            value={formatDate(inmueble.fecha_ultimo_contacto)}
          />
          <InfoBlock
            label="Ocupado por"
            value={inmueble.ocupado_por || "—"}
          />
          <InfoBlock
            label="Días desde contacto"
            value={
              inmueble.dias_ultimo_contacto !== null
                ? `${inmueble.dias_ultimo_contacto} días`
                : "—"
            }
          />
          <InfoBlock
            label="Última venta/alquiler"
            value={formatDate(inmueble.fecha_ultima_venta_alquiler)}
          />
          <InfoBlock
            label="Última modificación"
            value={formatDate(inmueble.fecha_ultima_modificacion)}
          />
          <InfoBlock
            label="Zona asignada"
            value={inmueble.zona_asignada || "—"}
          />
        </div>

        {/* Zona inferior: dos contenedores 50% */}
        <div className="flex gap-4">
          {/* Contenedor izquierdo */}
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Planta" value={inmueble.planta} />
            <EditableSection label="Puerta" value={inmueble.puerta} />
            <EditableSection label="Interior" value={inmueble.interior || "—"} />
            <EditableSection label="Referencia Catastral" value={inmueble.ref_catastral || "—"} />
            <EditableSection label="Certificado Energético" value={inmueble.estado_certificado || "—"} />
            <EditableSection label="Consumo Energía" value={inmueble.consumo_energia || "—"} />
            <EditableSection label="Emisiones CO2" value={inmueble.emisiones_CO2 || "—"} />
            <EditableSection label="Nota Simple" value={inmueble.nota_simple ? "Sí" : "No"} />
          </div>

          {/* Contenedor derecho */}
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableSection label="Habitaciones" value={inmueble.habitaciones} />
            <EditableSection label="Baños" value={inmueble.banos} />
            <EditableSection label="Estancias" value={inmueble.estancias} />
            <EditableSection label="Balcón" value={inmueble.balcon ? "Sí" : "No"} />
            <EditableSection label="Jardín" value={inmueble.jardin ? "Sí" : "No"} />
            <EditableSection label="Día de Zona" value={formatDate(inmueble.dia_zona)} />
            <EditableSection label="ID Edificio" value={inmueble.edificio_id} />
            <EditableSection label="ID Inquilino" value={inmueble.inquilino_id || "—"} />
            <EditableSection label="ID Propietario" value={inmueble.propietario_id || "—"} />
          </div>
        </div>
      </div>

      {/* Sección derecha: 1/3 */}
      <div className="w-1/3 space-y-4">
        <h3 className="text-lg font-semibold">Actividades recientes</h3>
        {inmueble.actividades?.length > 0 ? (
          inmueble.actividades.map((act, idx) => (
            <div
              key={idx}
              className="bg-white shadow p-4 rounded border hover:border-blue-500"
            >
              <p className="text-sm text-gray-500">
                {formatDate(act.fecha)} — {act.tipo}
              </p>
              <p className="font-medium">{act.descripcion || "Sin descripción"}</p>
            </div>
          ))
        ) : (
          <div className="bg-white shadow p-4 rounded border text-sm text-gray-500">
            No hay actividades registradas para este inmueble.
          </div>
        )}
      </div>
    </div>
  );
}

// Subcomponentes reutilizables
function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableSection({ label, value }) {
  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      <p className="text-sm">{value}</p>
      <button className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition">
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}
