        {/* 沐ｹ DATOS DEL PROPIETARIO */}
        {inmueble.propietario && (
          <div className="bg-white rounded shadow p-2.5">
            <h3 className="text-lg font-semibold mb-1.5">
              Datos del propietario
            </h3>

            <div className="grid grid-cols-2 gap-1.5">
              {[
                { key: "nombre", label: "Nombre" },
                { key: "apellido1", label: "Apellido 1" },
                { key: "apellido2", label: "Apellido 2" },
                { key: "telefono", label: "Telﾃｩfono" },
                { key: "telefono_movil", label: "Mﾃｳvil" },
                { key: "email", label: "Email" },
                { key: "direccion", label: "Direcciﾃｳn" },
                { key: "poblacion", label: "Poblaciﾃｳn" },
              ].map((field) => (
                <EditableSection
                  key={field.key}
                  label={field.label}
                  value={inmueble.propietario[field.key] || "窶能

Guardando..."
              />
            </div>
          </div>
        )}

        {/* MAPA */}
        <PropertyMap
          latitud={inmueble.edificio?.latitud}
          longitud={inmueble.edificio?.longitud}
          direccion={`${inmueble.edificio?.calle || ""} ${inmueble.edificio?.numero_calle || ""}`}
        />
      </div>
    </div>
  );
}

