import { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function PropertyMap({ latitud, longitud, direccion }) {
  useEffect(() => {
    if (!latitud || !longitud) return;

    // --- Recuperar última capa usada ---
    const lastLayer = localStorage.getItem("propertyMapLayer") || "street";

    // --- Inicializar mapa ---
    const map = L.map("property-map", {
      center: [latitud, longitud],
      zoom: 16,
      zoomControl: true, // ✅ Botones + y - visibles
    });

    // --- Capas disponibles ---
    const street = L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "", // ✅ Eliminamos footer irrelevante
    });

    const satellite = L.tileLayer(
      "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
      {
        attribution: "", // ✅ Eliminamos footer irrelevante
      }
    );

    // --- Añadir capa persistente ---
    if (lastLayer === "satellite") {
      satellite.addTo(map);
    } else {
      street.addTo(map);
    }

    // --- Control para cambiar capa y persistir selección ---
    const baseMaps = {
      "Vista de calles": street,
      "Vista satélite": satellite,
    };

    const layerControl = L.control.layers(baseMaps).addTo(map);

    map.on("baselayerchange", function (e) {
      const selected = e.name === "Vista satélite" ? "satellite" : "street";
      localStorage.setItem("propertyMapLayer", selected);
    });

    // --- Marcador principal ---
    const marker = L.marker([latitud, longitud])
      .addTo(map)
      .bindPopup(`<b>${direccion || "Ubicación del inmueble"}</b>`)
      .openPopup();

    // --- Botón de reinicio de vista ---
    const resetView = L.control({ position: "topleft" });
    resetView.onAdd = function () {
      const btn = L.DomUtil.create("button", "leaflet-bar leaflet-control");
      btn.innerHTML = "⟳";
      btn.style.background = "white";
      btn.style.cursor = "pointer";
      btn.style.width = "30px";
      btn.style.height = "30px";
      btn.onclick = () => {
        map.setView([latitud, longitud], 16);
        marker.openPopup();
      };
      return btn;
    };
    resetView.addTo(map);

    return () => {
      map.remove();
    };
  }, [latitud, longitud, direccion]);

  return (
    <div
      id="property-map"
      className="w-full h-56 rounded shadow border"
      style={{ minHeight: "300px" }}
    />
  );
}
