import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import SearchCliente from "./SearchCliente"; // ‚úÖ para elegir cliente
import SecondaryButton from "./SecondaryButton";
import SaveButton from "./SaveButton";

export default function NewPedidoForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    cliente_id: null,
    tipo_pedido: "",
    descripcion: "",
    presupuesto: "",
    fecha_entrega: "",
    estado: "Pendiente",
    prioridad: "Normal",
  });

  const [enviando, setEnviando] = useState(false);
  const [error, setError] = useState(null);

  // üîπ Manejo de cambios en inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // üîπ Guardar pedido en la API
  const handleGuardar = async () => {
    setError(null);

    if (!formData.cliente_id || !formData.tipo_pedido) {
      return setError("El cliente y el tipo de pedido son obligatorios.");
    }

    setEnviando(true);
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) throw new Error("No est√°s autenticado.");

      await api.post("/pedidos/", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      navigate("/pedidos");
    } catch (err) {
      console.error("Error creando pedido:", err);
      setError(
        err.response?.data
          ? JSON.stringify(err.response.data, null, 2)
          : err.message
      );
    } finally {
      setEnviando(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center z-50">
      <div className="bg-white w-2/3 p-6 rounded-lg shadow-xl space-y-4 relative">
        <h2 className="text-lg font-semibold">Nuevo Pedido</h2>

        {/* Cliente */}
        <div>
          <label className="block text-sm font-medium mb-1">Cliente</label>
          <SearchCliente
            selected={formData.cliente_id}
            onSelect={(cliente) =>
              setFormData((prev) => ({ ...prev, cliente_id: cliente.id }))
            }
          />
        </div>

        {/* Datos del pedido */}
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium">Tipo de Pedido</label>
            <input
              name="tipo_pedido"
              value={formData.tipo_pedido}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
              placeholder="Ej: Compra, Alquiler, Solicitud..."
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Presupuesto (‚Ç¨)</label>
            <input
              type="number"
              name="presupuesto"
              value={formData.presupuesto}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
              placeholder="Ej: 150000"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Fecha de entrega</label>
            <input
              type="date"
              name="fecha_entrega"
              value={formData.fecha_entrega}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Estado</label>
            <select
              name="estado"
              value={formData.estado}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
            >
              <option value="Pendiente">Pendiente</option>
              <option value="En curso">En curso</option>
              <option value="Completado">Completado</option>
              <option value="Cancelado">Cancelado</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium">Prioridad</label>
            <select
              name="prioridad"
              value={formData.prioridad}
              onChange={handleChange}
              className="w-full border rounded px-3 py-2"
            >
              <option value="Baja">Baja</option>
              <option value="Normal">Normal</option>
              <option value="Alta">Alta</option>
              <option value="Urgente">Urgente</option>
            </select>
          </div>
        </div>

        {/* Descripci√≥n */}
        <div>
          <label className="block text-sm font-medium">Descripci√≥n</label>
          <textarea
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            placeholder="Detalles o comentarios del pedido..."
          />
        </div>

        {/* Error */}
        {error && (
          <pre className="text-red-600 whitespace-pre-wrap">{error}</pre>
        )}

        {/* Botones */}
        <div className="flex justify-end gap-4">
          <button
            onClick={() => navigate("/pedidos")}
            className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
          >
            Cancelar
          </button>
          <button
            onClick={handleGuardar}
            disabled={enviando}
            className={`px-4 py-2 rounded text-white ${
              enviando ? "bg-blue-400" : "bg-blue-600 hover:bg-blue-700"
            }`}
          >
            {enviando ? "Guardando..." : "Guardar"}
          </button>
        </div>
      </div>
    </div>
  );
}
