import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Pencil } from "lucide-react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";
import SearchCliente from "./SearchCliente"; // reutilizamos el buscador de clientes

export default function PropertyDetail() {
  const { id } = useParams();
  const [inmueble, setInmueble] = useState(null);
  const [actividades, setActividades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const [editData, setEditData] = useState({});
  const [hasChanges, setHasChanges] = useState(false);

  const fetchInmueble = async () => {
    try {
      const res = await api.get(`/inmuebles/${id}/`);
      setInmueble(res.data);
      setEditData({});
      setHasChanges(false);
    } catch (err) {
      console.error(err);
      setError("Error al cargar el inmueble.");
    }
  };

  const fetchActividades = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const res = await api.get(`/actividades/inmueble/${id}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setActividades(res.data);
    } catch (err) {
      console.error("Error cargando actividades del inmueble:", err);
    }
  };

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      setError(null);
      try {
        await fetchInmueble();
        await fetchActividades();
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id]);

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("es-ES") : "—";

  const updateField = (field, value) => {
    setEditData((prev) => {
      const updated = { ...prev, [field]: value };
      setHasChanges(true);
      return updated;
    });
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      if (!token) throw new Error("No autenticado");

      await api.patch(`/inmuebles/${id}/`, editData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      await fetchInmueble();
    } catch (err) {
      console.error("Error guardando cambios:", err);
      alert("No se pudo guardar. Revisa la consola.");
    }
  };

  if (loading) return <div>Cargando inmueble y actividades...</div>;
  if (error)
    return (
      <div className="text-red-600 p-4 bg-red-100 border border-red-300 rounded">
        {error}
      </div>
    );
  if (!inmueble) return <div>Datos no disponibles</div>;

  const currentData = { ...inmueble, ...editData };

  return (
    <div className="flex gap-4 w-full relative">
      {/* === Izquierda === */}
      <div className="w-2/3 space-y-4">
        {/* Dirección */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-bold">
            {`${currentData.edificio?.calle || ""} ${
              currentData.edificio?.numero_calle || ""
            }, Planta ${currentData.planta || ""}, Puerta ${
              currentData.puerta || ""
            }`}
          </h2>
        </div>

        {/* Botones Estado CRM */}
        <div className="flex gap-2">
          {["Por defecto", "Noticia", "Encargo"].map((estado) => {
            const isActive = currentData.estado_CRM === estado;
            return (
              <button
                key={estado}
                onClick={() => updateField("estado_CRM", estado)}
                className={`px-4 py-2 rounded border ${
                  isActive
                    ? "bg-blue-600 text-white border-blue-700"
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                }`}
              >
                {estado === "Por defecto" ? "Inmueble" : estado}
              </button>
            );
          })}
        </div>

        {/* Información principal */}
        <h3 className="text-md font-semibold">Información principal</h3>
        <div className="bg-gray-50 rounded shadow p-4 grid grid-cols-3 grid-rows-2 gap-4">
          <InfoBlock label="Último contacto" value={formatDate(currentData.fecha_ultimo_contacto)} />
          <InfoBlock label="Ocupado por" value={currentData.ocupado_por || "—"} />
          <InfoBlock label="Actividades" value={actividades.length} />
          <InfoBlock label="Última venta/alquiler" value={formatDate(currentData.fecha_ultima_venta_alquiler)} />
          <InfoBlock label="Última modificación" value={formatDate(currentData.fecha_ultima_modificacion)} />
          <InfoBlock label="Zona asignada" value={currentData.zona_asignada || "—"} />
        </div>

        {/* Información y características del inmueble */}
        <h3 className="text-md font-semibold">Información y características del inmueble</h3>
        <div className="flex gap-4">
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableField label="Planta" value={currentData.planta} onSave={(val) => updateField("planta", val)} />
            <EditableField label="Puerta" value={currentData.puerta} onSave={(val) => updateField("puerta", val)} />
            <EditableField label="Interior" value={currentData.interior || "—"} onSave={(val) => updateField("interior", val)} />
            <EditableField label="Certificado Energético" value={currentData.estado_certificado || "—"} onSave={(val) => updateField("estado_certificado", val)} />
            <EditableField label="Consumo Energía" value={currentData.consumo_energia ?? "—"} onSave={(val) => updateField("consumo_energia", val)} />
          </div>
          <div className="w-1/2 bg-white border rounded shadow divide-y">
            <EditableField label="Habitaciones" value={currentData.habitaciones} onSave={(val) => updateField("habitaciones", val)} />
            <EditableField label="Baños" value={currentData.banos} onSave={(val) => updateField("banos", val)} />
            <EditableField label="Balcón" value={currentData.balcon ? "Sí" : "No"} onSave={(val) => updateField("balcon", val === "Sí")} />
            <EditableField label="Jardín" value={currentData.jardin ? "Sí" : "No"} onSave={(val) => updateField("jardin", val === "Sí")} />
            <div className="p-4">
              <p className="text-xs text-gray-500 mb-1">Propietario</p>
              <SearchCliente
                initialId={currentData.propietario?.id}
                onSelect={(cliente) => updateField("propietario", cliente?.id || null)}
              />
            </div>
          </div>
        </div>

        {/* Footer con guardar/cancelar */}
        {hasChanges && (
          <div className="bg-yellow-50 border-t border-yellow-300 p-4 flex justify-between items-center">
            <span className="text-sm text-yellow-800">Hay cambios sin guardar.</span>
            <div className="flex gap-2">
              <button
                onClick={() => {
                  setEditData({});
                  setHasChanges(false);
                }}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
              >
                Cancelar
              </button>
              <button
                onClick={handleSave}
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              >
                Guardar
              </button>
            </div>
          </div>
        )}
      </div>

      {/* === Derecha (sin cambios, como ya estaba) === */}
      <div className="w-1/3 space-y-4">
        {/* ... actividades igual que antes ... */}
      </div>
    </div>
  );
}

function InfoBlock({ label, value }) {
  return (
    <div>
      <p className="text-sm text-gray-500">{label}</p>
      <p className="font-medium">{value}</p>
    </div>
  );
}

function EditableField({ label, value, onSave }) {
  const [editing, setEditing] = useState(false);
  const [localValue, setLocalValue] = useState(value);

  return (
    <div className="p-4 relative group">
      <p className="text-xs text-gray-500 mb-1">{label}</p>
      {editing ? (
        <input
          type="text"
          value={localValue}
          onChange={(e) => setLocalValue(e.target.value)}
          onBlur={() => {
            onSave(localValue);
            setEditing(false);
          }}
          className="border rounded px-2 py-1 w-full"
        />
      ) : (
        <p className="text-sm">{value}</p>
      )}
      <button
        onClick={() => setEditing(true)}
        className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition"
      >
        <Pencil size={16} className="text-blue-500" />
      </button>
    </div>
  );
}
