import { useState, useEffect } from "react";
import api from "../api";

export default function SearchCliente({ onSelect, selected }) {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [searching, setSearching] = useState(false);
  const [selectedCliente, setSelectedCliente] = useState(selected || null);

  // ✅ Capitaliza correctamente los nombres
  const formatNombre = (cliente) => {
    const raw =
      cliente.nombre_apellidos_completo ||
      `${cliente.nombre} ${cliente.apellido1} ${cliente.apellido2 || ""}`;
    return raw
      .split(" ")
      .map((w) => w.charAt(0).toUpperCase() + w.slice(1).toLowerCase())
      .join(" ")
      .trim();
  };

  // ✅ Búsqueda de clientes con debounce
  useEffect(() => {
    if (!query || selectedCliente) {
      setResults([]);
      return;
    }

    const delay = setTimeout(async () => {
      setSearching(true);
      try {
        const token = localStorage.getItem("accessToken");
        if (!token) throw new Error("No autenticado");

        const res = await api.get(`/clientes/?search=${encodeURIComponent(query)}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const ordered = res.data.sort((a, b) => {
          const qa =
            a.nombre_apellidos_completo ||
            `${a.nombre} ${a.apellido1} ${a.apellido2 || ""}`.toLowerCase();
          const qb =
            b.nombre_apellidos_completo ||
            `${b.nombre} ${b.apellido1} ${b.apellido2 || ""}`.toLowerCase();
          const q = query.toLowerCase();
          const startsA = qa.startsWith(q);
          const startsB = qb.startsWith(q);
          if (startsA && !startsB) return -1;
          if (!startsA && startsB) return 1;
          return qa.localeCompare(qb);
        });

        setResults(ordered);
      } catch (err) {
        console.error("Error buscando clientes:", err);
        setResults([]);
      } finally {
        setSearching(false);
      }
    }, 400);

    return () => clearTimeout(delay);
  }, [query, selectedCliente]);

  const handleSelect = (cliente) => {
    setSelectedCliente(cliente);
    setQuery(formatNombre(cliente));
    setResults([]);
    if (onSelect) onSelect(cliente);
  };

  const handleClearSelection = () => {
    setSelectedCliente(null);
    setQuery("");
    if (onSelect) onSelect(null);
  };

  return (
    <div className="space-y-1">
      <label className="block text-sm font-medium">Buscar cliente</label>

      {selectedCliente ? (
        <div className="flex items-center justify-between bg-gray-100 border rounded px-3 py-2">
          <span className="text-sm">{formatNombre(selectedCliente)}</span>
          <button
            onClick={handleClearSelection}
            className="text-xs text-red-600 hover:underline"
          >
            Cambiar
          </button>
        </div>
      ) : (
        <>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Nombre o apellidos..."
            className="w-full border rounded px-3 py-2"
          />
          {searching && (
            <p className="text-xs text-gray-500 mt-1">Buscando...</p>
          )}
          {results.length > 0 && (
            <div className="border mt-1 rounded bg-white max-h-40 overflow-auto shadow-md">
              {results.map((r) => (
                <div
                  key={r.id}
                  onClick={() => handleSelect(r)}
                  className="px-3 py-2 hover:bg-blue-50 cursor-pointer text-sm"
                >
                  {formatNombre(r)}
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}
