import { useState, useEffect, useRef } from "react";
import api from "../api";
import NewActivityForm from "./NewActivityForm";
import {
  startHour,
  endHour,
  intervalMinutes,
  getDateFromSelection,
  getStyleForActividad,
} from "./calendarUtils";

const totalRows = ((endHour - startHour) * 60) / intervalMinutes;

const tipoColores = {
  "Adquisición": "bg-yellow-400 text-black",
  "Visita": "bg-blue-500 text-white",
  "Llamada": "bg-green-500 text-white",
  "Contacto directo": "bg-red-500 text-white",
  "Genérica": "bg-gray-500 text-white",
  "Zona": "bg-orange-500 text-white",
  "Reunión o curso": "bg-purple-600 text-white",
};

export default function DayView({ usuariosSeleccionados }) {
  const [formVisible, setFormVisible] = useState(false);
  const [formPosition, setFormPosition] = useState({ x: 0, y: 0 });
  const [selection, setSelection] = useState(null);
  const [selectedCells, setSelectedCells] = useState([]);
  const [actividades, setActividades] = useState([]);
  const [actividadEditando, setActividadEditando] = useState(null);
  const calendarRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [startCell, setStartCell] = useState(null);
  const [dragId, setDragId] = useState(null);
  const [resizingId, setResizingId] = useState(null);
  const dragOffsetY = useRef(0);

  useEffect(() => {
    const fetchActividades = async () => {
      const token = localStorage.getItem("accessToken");
      if (!token) return;
      try {
        const res = await api.get("/actividades/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setActividades(res.data);
      } catch (err) {
        console.error("Error obteniendo actividades:", err);
      }
    };

    fetchActividades();
  }, [formVisible]);

  const handleMouseDown = (rowIndex) => {
    setIsDragging(true);
    setStartCell({ rowIndex });
    setSelectedCells([{ rowIndex }]);
  };

  const handleMouseEnter = (rowIndex) => {
    if (isDragging && startCell) {
      const minRow = Math.min(startCell.rowIndex, rowIndex);
      const maxRow = Math.max(startCell.rowIndex, rowIndex);
      const newSelection = [];
      for (let i = minRow; i <= maxRow; i++) {
        newSelection.push({ rowIndex: i });
      }
      setSelectedCells(newSelection);
    }
  };

  const handleMouseUp = (endRowIndex) => {
    if (isDragging && startCell) {
      const minRow = Math.min(startCell.rowIndex, endRowIndex);
      const maxRow = Math.max(startCell.rowIndex, endRowIndex);

      const top = minRow * 20 + 64;
      const left = 160;

      setFormPosition({ x: left, y: top });
      setSelection({ day: "Hoy", start: minRow, end: maxRow });
      setFormVisible(true);
      setActividadEditando(null);
    }
    setIsDragging(false);
    setStartCell(null);
  };

  const isCellSelected = (rowIndex) => {
    return selectedCells.some((cell) => cell.rowIndex === rowIndex);
  };

  const datosTiempo = selection ? getDateFromSelection(selection) : null;

  const actividadesHoy = actividades.filter((a) => {
    const inicio = new Date(a.fecha_inicio);
    const hoy = new Date();
    return (
      (!usuariosSeleccionados.length ||
        usuariosSeleccionados.includes(a.usuario_responsable?.id)) &&
      inicio.toDateString() === hoy.toDateString()
    );
  });

  const handleMouseMove = (e) => {
    if (!dragId && !resizingId) return;
    const deltaY = Math.round(e.movementY / 20) * 20;
    if (deltaY === 0) return;

    setActividades((prev) =>
      prev.map((a) => {
        if (a.id === dragId) {
          const inicio = new Date(a.fecha_inicio);
          const fin = new Date(a.fecha_fin);
          inicio.setMinutes(inicio.getMinutes() + (deltaY / 20) * intervalMinutes);
          fin.setMinutes(fin.getMinutes() + (deltaY / 20) * intervalMinutes);

          if (inicio.getHours() < startHour || fin.getHours() > endHour) return a;

          guardarCambiosActividad(a.id, inicio, fin);
          return { ...a, fecha_inicio: inicio.toISOString(), fecha_fin: fin.toISOString() };
        }

        if (a.id === resizingId) {
          const fin = new Date(a.fecha_fin);
          fin.setMinutes(fin.getMinutes() + (deltaY / 20) * intervalMinutes);

          const inicio = new Date(a.fecha_inicio);
          if ((fin - inicio) / 60000 < intervalMinutes) return a;

          if (fin.getHours() > endHour) return a;

          guardarCambiosActividad(a.id, inicio, fin);
          return { ...a, fecha_fin: fin.toISOString() };
        }

        return a;
      })
    );
  };

  const guardarCambiosActividad = async (id, fecha_inicio, fecha_fin) => {
    const token = localStorage.getItem("accessToken");
    try {
      await api.put(`/actividades/${id}/`, {
        fecha_inicio: fecha_inicio.toISOString(),
        fecha_fin: fecha_fin.toISOString(),
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
    } catch (err) {
      console.error("Error actualizando actividad:", err);
    }
  };

  useEffect(() => {
    const handleMouseUpGlobal = () => {
      setDragId(null);
      setResizingId(null);
    };

    window.addEventListener("mouseup", handleMouseUpGlobal);
    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mouseup", handleMouseUpGlobal);
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, [dragId, resizingId]);

  return (
    <div className="flex-1 overflow-auto relative select-none" ref={calendarRef}>
      <table className="w-full border-collapse">
        <thead className="sticky top-0 bg-white z-10">
          <tr>
            <th className="w-20" />
            <th className="text-xs font-semibold text-center border-l">
              <div>Hoy</div>
              <div className="text-[10px] text-gray-400">
                {new Date().toLocaleDateString()}
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: totalRows }).map((_, i) => {
            const hour = Math.floor((i * intervalMinutes) / 60) + startHour;
            const minute = (i * intervalMinutes) % 60;
            const timeLabel = `${hour.toString().padStart(2, "0")}:${minute
              .toString()
              .padStart(2, "0")}`;

            return (
              <tr key={i} className="h-5 border-b">
                <td className="text-xs pr-2 text-right w-20 text-gray-500 align-top">
                  {timeLabel}
                </td>
                <td
                  className={`border-l cursor-pointer ${
                    isCellSelected(i) ? "bg-black" : "hover:bg-blue-50"
                  }`}
                  onMouseDown={() => handleMouseDown(i)}
                  onMouseEnter={() => handleMouseEnter(i)}
                  onMouseUp={() => handleMouseUp(i)}
                />
              </tr>
            );
          })}
        </tbody>
      </table>

      {actividadesHoy.map((a) => {
        const style = getStyleForActividad(a);
        const claseColor = tipoColores[a.tipo] || "bg-black text-white";

        return (
          <div
            key={a.id}
            className={`absolute rounded text-xs p-1 ${claseColor} cursor-pointer`}
            style={{
              top: style.top,
              left: 160,
              height: style.height,
              width: `calc(100% - 160px)`,
            }}
            onMouseDown={(e) => {
              if (e.target.dataset.handle === "resize") {
                setResizingId(a.id);
              } else {
                setDragId(a.id);
              }
              e.preventDefault();
            }}
            onDoubleClick={() => {
              setActividadEditando(a);
              setFormPosition({ x: 160, y: style.top });
              setFormVisible(true);
            }}
          >
            {a.descripcion_publica}
            <div
              data-handle="resize"
              className="w-1/3 h-2 bg-white absolute bottom-0 left-1/3 cursor-s-resize rounded-sm"
            ></div>
          </div>
        );
      })}

      {formVisible && (
        <div
          className="absolute z-50"
          style={{ top: formPosition.y, left: formPosition.x }}
        >
          <NewActivityForm
            actividad={actividadEditando}
            fechaInicial={datosTiempo?.fecha}
            horaInicioInicial={datosTiempo?.horaInicio}
            horaFinInicial={datosTiempo?.horaFin}
            onCancel={() => {
              setFormVisible(false);
              setSelectedCells([]);
              setActividadEditando(null);
            }}
            onCreated={() => {
              setFormVisible(false);
              setSelectedCells([]);
              setActividadEditando(null);
            }}
          />
        </div>
      )}
    </div>
  );
}
