import { useState, useEffect } from "react";

export default function NewActivityForm({
  clienteId = null,
  clienteNombre = "",
  clienteApellido1 = "",
  clienteApellido2 = "",
  onCreated = null,
  onCancel = null,
}) {
  const [tipo, setTipo] = useState("Visita");
  const [estado, setEstado] = useState("En proceso");
  const [descripcion, setDescripcion] = useState("");
  const [descripcionPublica, setDescripcionPublica] = useState("Sin descripción pública");
  const [error, setError] = useState(null);
  const [enviando, setEnviando] = useState(false);

  const [horaInicio, setHoraInicio] = useState("09:00");
  const [horaFin, setHoraFin] = useState("10:00");
  const [fecha, setFecha] = useState(() => {
    const hoy = new Date();
    return hoy.toISOString().slice(0, 10);
  });
  const [usuarioId, setUsuarioId] = useState(null);

  // Actualizamos descripcionPublica según estado y descripcion
  useEffect(() => {
    if (estado === "Realizada") {
      setDescripcionPublica(descripcion);
    } else {
      setDescripcionPublica("Sin descripción pública");
    }
  }, [estado, descripcion]);

  // Validar que horaFin sea posterior a horaInicio
  const validarHoras = () => {
    const [hInicio, mInicio] = horaInicio.split(":").map(Number);
    const [hFin, mFin] = horaFin.split(":").map(Number);

    if (hFin < hInicio) return false;
    if (hFin === hInicio && mFin <= mInicio) return false;
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!validarHoras()) {
      setError("La hora de fin debe ser mayor que la hora de inicio.");
      return;
    }

    setEnviando(true);

    const fechaInicioISO = new Date(`${fecha}T${horaInicio}:00`).toISOString();
    const fechaFinISO = new Date(`${fecha}T${horaFin}:00`).toISOString();

    const payload = {
      tipo,
      estado,
      descripcion_empleado: descripcion,
      descripcion_publica: descripcionPublica,
      fecha_inicio: fechaInicioISO,
      fecha_fin: fechaFinISO,
      cliente: clienteId || null,
      usuario_responsable: usuarioId,
    };

    console.log("Payload a enviar:", payload);

    try {
      const token = localStorage.getItem("access_token");
      const response = await fetch("http://localhost:8000/api/actividad/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(JSON.stringify(errorData));
      } else {
        if (onCreated) onCreated();
      }
    } catch (err) {
      setError("Error al crear la actividad: " + err.message);
    } finally {
      setEnviando(false);
    }
  };

  return (
    <form
      className="bg-white rounded-lg shadow-md p-6 w-1/4 mx-auto mt-6"
      onSubmit={handleSubmit}
    >
      <h2 className="text-lg font-bold mb-4">Nueva Actividad</h2>

      <div className="mb-4">
        <label className="block font-medium mb-1">Cliente</label>
        <input
          type="text"
          readOnly
          value={`${clienteNombre} ${clienteApellido1} ${clienteApellido2}`}
          className="w-full border rounded px-3 py-2 bg-gray-100 cursor-not-allowed"
        />
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Tipo</label>
        <select
          value={tipo}
          onChange={(e) => setTipo(e.target.value)}
          className="w-full border rounded px-3 py-2"
          required
        >
          <option value="Visita">Visita</option>
          <option value="Adquisición">Adquisición</option>
          <option value="Zona">Zona</option>
          <option value="Genérica">Genérica</option>
          <option value="Reunión/Curso">Reunión/Curso</option>
        </select>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Estado</label>
        <select
          value={estado}
          onChange={(e) => setEstado(e.target.value)}
          className="w-full border rounded px-3 py-2"
          required
        >
          <option value="En proceso">En proceso</option>
          <option value="Realizada">Realizada</option>
          <option value="Pendiente">Pendiente</option>
          <option value="No realizada">No realizada</option>
        </select>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Descripción</label>
        <textarea
          value={descripcion}
          onChange={(e) => {
            if (e.target.value.length <= 1024) setDescripcion(e.target.value);
          }}
          rows={4}
          className="w-full border rounded px-3 py-2"
          maxLength={1024}
          placeholder="Descripción para el empleado"
          required
        />
        <p className="text-xs text-gray-500 text-right">
          {1024 - descripcion.length} caracteres restantes
        </p>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Descripción pública</label>
        <textarea
          value={descripcionPublica}
          onChange={(e) => {
            if (estado === "Realizada" && e.target.value.length <= 1024) {
              setDescripcionPublica(e.target.value);
            }
          }}
          rows={4}
          className={`w-full border rounded px-3 py-2 ${
            estado !== "Realizada" ? "bg-gray-100 cursor-not-allowed" : ""
          }`}
          maxLength={1024}
          placeholder={
            estado !== "Realizada"
              ? "Disponible solo cuando la actividad está realizada"
              : ""
          }
          disabled={estado !== "Realizada"}
          required={estado === "Realizada"}
        />
        <p className="text-xs text-gray-500 text-right">
          {1024 - descripcionPublica.length} caracteres restantes
        </p>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Fecha</label>
        <input
          type="date"
          value={fecha}
          onChange={(e) => setFecha(e.target.value)}
          className="w-full border rounded px-3 py-2"
          required
        />
      </div>

      <div className="mb-4 flex space-x-4">
        <div className="flex-1">
          <label className="block font-medium mb-1">Hora inicio</label>
          <input
            type="time"
            value={horaInicio}
            onChange={(e) => setHoraInicio(e.target.value)}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>
        <div className="flex-1">
          <label className="block font-medium mb-1">Hora fin</label>
          <input
            type="time"
            value={horaFin}
            onChange={(e) => setHoraFin(e.target.value)}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Usuario responsable</label>
        <select
          value={usuarioId || ""}
          onChange={(e) => setUsuarioId(e.target.value || null)}
          className="w-full border rounded px-3 py-2"
          required
        >
          <option value="" disabled>
            Selecciona usuario
          </option>
          <option value="1">Usuario 1</option>
          <option value="2">Usuario 2</option>
          <option value="3">Usuario 3</option>
          {/* Ajusta los usuarios según tu backend */}
        </select>
      </div>

      {error && (
        <div className="mb-4 text-red-600 font-medium" role="alert">
          {error}
        </div>
      )}

      <div className="flex justify-end space-x-4">
        <button
          type="button"
          onClick={onCancel}
          className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400"
          disabled={enviando}
        >
          Cancelar
        </button>
        <button
          type="submit"
          disabled={enviando}
          className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700 disabled:opacity-50"
        >
          {enviando ? "Guardando..." : "Guardar"}
        </button>
      </div>
    </form>
  );
}
