// src/components/Calendar.jsx
import { useState, useRef, useEffect } from "react";
import NewCalendarActivity from "./NewCalendarActivity";

const startHour = 6;
const endHour = 24;
const intervalMinutes = 15;
const daysOfWeek = ["Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado", "Domingo"];

export default function Calendar() {
  const [formVisible, setFormVisible] = useState(false);
  const [formPosition, setFormPosition] = useState({ x: 0, y: 0 });
  const [selection, setSelection] = useState(null);
  const calendarRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [startCell, setStartCell] = useState(null);
  const [currentDate, setCurrentDate] = useState(new Date());

  const daysWithDates = daysOfWeek.map((day, index) => {
    const today = new Date();
    const monday = new Date(today.setDate(today.getDate() - today.getDay() + 1));
    const date = new Date(monday);
    date.setDate(monday.getDate() + index);
    return { day, date };
  });

  const handleMouseDown = (rowIndex, colIndex) => {
    setIsDragging(true);
    setStartCell({ rowIndex, colIndex });
  };

  const handleMouseUp = (endRowIndex, endColIndex) => {
    if (isDragging && startCell && startCell.colIndex === endColIndex) {
      const minRow = Math.min(startCell.rowIndex, endRowIndex);
      const maxRow = Math.max(startCell.rowIndex, endRowIndex);
      const col = startCell.colIndex;

      const top = minRow * 20 + 64;
      const left = col * 160 + 160;

      setFormPosition({ x: left, y: top });
      setSelection({
        day: daysWithDates[col].day,
        date: daysWithDates[col].date,
        startIndex: minRow,
        endIndex: maxRow,
      });
      setFormVisible(true);
    }
    setIsDragging(false);
    setStartCell(null);
  };

  const renderRows = () => {
    const rows = [];
    const totalRows = ((endHour - startHour) * 60) / intervalMinutes;

    for (let i = 0; i < totalRows; i++) {
      const hour = Math.floor(i * intervalMinutes / 60) + startHour;
      const minute = (i * intervalMinutes) % 60;
      const timeLabel = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;

      rows.push(
        <tr key={i} className="h-8 border-b">
          <td className="text-xs pr-2 text-right align-top w-20 text-gray-500">{timeLabel}</td>
          {daysWithDates.map((_, j) => {
            const isSelected =
              isDragging &&
              startCell &&
              j === startCell.colIndex &&
              i >= Math.min(startCell.rowIndex, selection?.startIndex ?? i + 1) &&
              i <= Math.max(startCell.rowIndex, selection?.endIndex ?? i - 1);

            return (
              <td
                key={j}
                className={`border-l cursor-pointer ${isSelected ? "bg-black" : "hover:bg-blue-50"}`}
                onMouseDown={() => handleMouseDown(i, j)}
                onMouseUp={() => handleMouseUp(i, j)}
              />
            );
          })}
        </tr>
      );
    }
    return rows;
  };

  const formattedHeader = new Intl.DateTimeFormat("es-ES", { month: "long", year: "numeric" }).format(currentDate);

  return (
    <div className="flex flex-col h-[calc(100vh-4rem)]">
      <div className="text-xl font-semibold text-center py-4 bg-gray-100 border-b">
        {formattedHeader.charAt(0).toUpperCase() + formattedHeader.slice(1)}
      </div>
      <div className="flex flex-1">
        {/* Filtros */}
        <div className="w-1/8 min-w-[160px] p-4 border-r bg-gray-50">
          <h3 className="font-semibold mb-2">Filtros</h3>
          <select className="w-full border rounded p-1 mb-2">
            <option>Usuario 1</option>
            <option>Usuario 2</option>
          </select>
        </div>

        {/* Calendario */}
        <div className="flex-1 overflow-auto relative" ref={calendarRef}>
          <table className="w-full border-collapse table-fixed">
            <thead className="sticky top-0 bg-white z-10">
              <tr>
                <th className="w-20" />
                {daysWithDates.map(({ day, date }, idx) => (
                  <th key={idx} className="text-xs font-semibold text-gray-600 text-center py-2 border-l w-[calc((100%-5rem)/7)]">
                    <div>{day}</div>
                    <div className="text-[11px] text-gray-500">
                      {date.getDate().toString().padStart(2, '0')}/{(date.getMonth() + 1).toString().padStart(2, '0')}
                    </div>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>{renderRows()}</tbody>
          </table>

          {formVisible && selection && (
            <NewCalendarActivity
              position={formPosition}
              selection={selection}
              onClose={() => setFormVisible(false)}
            />
          )}
        </div>
      </div>
    </div>
  );
}
