import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function PropertiesList() {
  const [inmuebles, setInmuebles] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:8000/api/inmuebles/")
      .then(res => setInmuebles(res.data))
      .catch(err => console.error(err));
  }, []);

  const goToDetail = (id) => {
    navigate(`/inmuebles/${id}`);
  };

  return (
    <div className="bg-white p-4 shadow rounded w-full">
      <h2 className="text-xl font-bold mb-4">Lista de Inmuebles</h2>
      <table className="w-full table-auto border-collapse">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="px-4 py-2">Edificio</th>
            <th className="px-4 py-2">Propietario</th>
            <th className="px-4 py-2">Planta</th>
            <th className="px-4 py-2">Puerta</th>
            <th className="px-4 py-2">Habitaciones</th>
            <th className="px-4 py-2">Baños</th>
            <th className="px-4 py-2">Balcón</th>
            <th className="px-4 py-2">Jardín</th>
          </tr>
        </thead>
        <tbody>
          {inmuebles.map((inmueble) => (
            <tr
              key={inmueble.id}
              className="hover:bg-gray-100 cursor-pointer"
              onClick={() => goToDetail(inmueble.id)}
            >
              <td className="px-4 py-2">
                {inmueble.edificio
                  ? `${inmueble.edificio.calle} ${inmueble.edificio.numero_calle}`
                  : "Sin dirección"}
              </td>
              <td className="px-4 py-2">
                {inmueble.propietario
                  ? `${inmueble.propietario.nombre} ${inmueble.propietario.apellido1}`
                  : "Sin propietario"}
              </td>
              <td className="px-4 py-2">{inmueble.planta}</td>
              <td className="px-4 py-2">{inmueble.puerta}</td>
              <td className="px-4 py-2">{inmueble.habitaciones}</td>
              <td className="px-4 py-2">{inmueble.banos}</td>
              <td className="px-4 py-2">{inmueble.balcon ? "Sí" : "No"}</td>
              <td className="px-4 py-2">{inmueble.jardin ? "Sí" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
